--
-- Cấu trúc bảng cho bảng `tbl_about_us`
--

CREATE TABLE `tbl_about_us` (
  `Content` longtext COLLATE utf8_unicode_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tbl_about_us`
--

INSERT INTO `tbl_about_us` (`Content`) VALUES
('<p>\r\n                Trước hết Vietnam Tourist xin gửi lời chào trân trọng và lời cảm ơn tới quý khách hàng gần xa đã ghé thăm Website,\r\n                sử dụng dịch vụ\r\n                <b>du lịch</b>\r\n                của chúng tôi trong suốt thời gian qua.\r\n            </p>\r\n            <p>\r\n                Để có được thành công, uy tín và chỗ đứng trên thị trường như hiện nay\r\n                là nhờ sự cố gắng không ngừng nghỉ của toàn thể CBNV Trung Tâm, chúng tôi đã đồng sức đồng lòng để xây dựng các sản phẩm tour du lịch phù hợp với khách hàng, thiết lập mối quan hệ chặt chẽ với các nhà cung cấp như khách sạn, vé máy bay, …\r\n                để mang đến cho quý khách những dịch vụ tốt nhất với giá rẻ nhất.\r\n            </p>\r\n            <p>Với chúng tôi,\r\n                <b>DU LỊCH</b>\r\n                không chỉ là để nghỉ ngơi thư giãn mà còn là dịp để kết nối các mối quan hệ với nhau,\r\n                tăng cường sự đoàn kết của đồng nghiệp, gia đình gắn bó, bạn bè thấu hiểu.\r\n            </p>\r\n            <p>\r\n                Công ty bạn đang kinh doanh rất tốt, nhưng bạn muốn xây dựng một tổ chức có văn hóa\r\n                riêng đoàn kết, gắn bó. Hãy gọi cho chúng tôi ngay để được tư vấn.\r\n            </p>\r\n            <p>\r\n                Bạn đang trong guồng quay của công việc, cuộc sống, không có nhiều thời gian chăm sóc cho gia đình, bạn muốn bù đăp cho những người thân yêu bằng 1 kỳ nghỉ ngắn,\r\n                một buổi đi chơi xa, đừng ngần ngại, hãy gọi cho chúng tôi.\r\n            </p>\r\n            <p>Với nhiều năm kinh nghiệm và đội ngũ nhân viên năng động, nhiệt tình\r\n                <b>TravelThree</b> sẽ luôn là một người bạn đồng hàng đáng tin cậy cho quý khách.\r\n            </p>');

