INSERT INTO `tbl_activities` (`id_activities`, `Name_LH`, `Details`, `Picture`) VALUES
(13, 'Du lịch văn hóa', 'Du lịch lễ hội, du lịch hoa: điển  hình như Festival Huế, Festival hoa Đà Lạt, hội chùa Hương, hội Lim, tết cổ truyền… Với loại hình du lịch này du khách có thể vừa tham quan vừa kết hợp du lịch văn hóa. Đặc biệt là với du khách quốc tế.', 'dalat.jpg'),
(14, 'Du lịch tham quan', 'Tham quan di tích lịch sử, thắng cảnh: Đây là hình thức du lịch truyền thống ở Việt Nam. Việt Nam có được  sự đa dạng và phong phú của yếu tố tự nhiên. Danh lam thắng cảnh trải đều ở 64 tỉnh thành trong cả nước. Những điểm du lịch nổi tiếng được nhắc đến ', 'hue.jpg'),
(15, 'Du lịch biển', 'Với hơn 3.400km đường bờ biển, Việt Nam là một trong những quốc gia có ưu thế trong phát triển du lịch biển kết hợp khám phá, nghỉ dưỡng. Vẻ đẹp hoang sơ của những bờ cát trắng tinh, vịnh biển hoang sơ hay những hòn đảo nhiệt đới quanh năm tươi tốt, những', 'phuyen.jpg'),
(16, 'Du lịch xanh', 'Du lịch sinh thái: các khu du lịch sinh thái nổi tiếng của Việt Nam có thể kể đến như Nhà vườn Huế, bãi biển Lăng Cô, rừng Cúc Phương, hồ Ba Bể, vườn quốc gia Cát Tiên, U Minh Thượng, U Minh Hạ…\r\n \r\n\r\nDu lịch nghỉ dưỡng và chữa bệnh: tắm nước khoáng Kim B', 'danang.jpg'),
(17, 'Du lịch ẩm thực', ' Những bữa tiệc cung đình Huế hay ẩm thực Bắc Trung Nam… Nét tinh tế của ẩm thực Việt Nam chịu sự ảnh hưởng rất lớn của yếu tố lịch sử, khí hậu, điều kiện tự nhiên…', 'cantho.jpg');

