--

INSERT INTO `tbl_destination` (`id_destination`, `Name_city`, `Details`, `Picture`) VALUES
(1, 'Hà Nội', 'Do có kiểu khí hậu nhiệt đới gió mùa ẩm nên đến du lịch Hà Nội, bạn có thể thưởng thức đủ 4 mùa trong năm. Mỗi mùa đều có những đặc trưng riêng, cho bạn những cảm nhận khác nhau về cuộc sống, về cảnh vật và con người nơi đây. Hà Nội vào đông lạnh thì cũng', 'gioi-thieu-ve-ha-noi-1.jpg'),
(2, 'Đà Lạt', 'Đà Lạt là một trong những thành phố du lịch nổi tiếng nhất của Việt Nam. Một thành phố trực thuộc tỉnh và tỉnh lị Lâm Đồng, nằm trên cao nguyên Lâm Viên, có độ cao so với mặt nước biển là 1.500m và diện tích tự nhiên là 393,29 km2', '237.jpg'),
(3, 'Đà Nẵng', 'Đà Nẵng được xem là điểm trung chuyển quan trọng trên con đường di sản miền Trung, thành phố Đà Nẵng được bao bọc bởi 3 di sản văn hóa thế giới: Huế, Hội An, Mỹ Sơn. Xa hơn một chút nữa là di sản thiên nhiên thế giới Vườn Quốc gia Phong Nha – Kẻ Bàng và Đ', 'danang_1543552135.jpeg'),
(4, 'Ninh Bình', 'Ninh Bình là tỉnh có tiềm năng du lịch đa dạng và phong phú với nhiều danh hiệu quốc tế như: di sản thế giới Tràng An, khu dự trữ sinh quyển thế giới Cồn Nổi, Khu Ramsar thế giới đầm Vân Long. Địa danh này nằm ở vị trí cửa ngõ cực nam của tam giác châu th', 'kinh-nghiem-du-lich-trang-an.jpg'),
(5, 'Nha Trang', 'Thành phố biển Nha Trang là thủ phủ của tỉnh Khánh Hòa, thuộc miền duyên hải Nam Trung bộ Việt Nam. Vịnh biển Nha Trang là một trong những vịnh biển đẹp nhất thế giới, đó là món quà vô giá mà thiên nhiên ban tặng cho vùng đất này. ... bên kia đường là bãi', 'nha-trang.jpg'),
(6, 'Phú Quốc', 'Phú Quốc là quần đảo xinh đẹp nằm sâu trong vùng vịnh Thái Lan, thuộc tỉnh Kiên Giang. Ở vùng biển phía Nam của tổ quốc, đảo Ngọc Phú Quốc – hòn đảo lớn nhất của Việt Nam, cũng là đảo lớn nhất trong quần thể 22 đảo tại đây. Nước biển trong vắt, những dòng', '9.jpg');

