INSERT INTO `tbl_news` (`id_news`, `Title`, `Content`, `Time_news`, `author`, `Picture`) VALUES
(1, 'Kinh nghiệm du lịch Đà Nẵng Ngắn ngày cho gia đình và cá nhân', 'Thành phố Đà Nẵng có sân bay quốc tế phục vụ các đường bay nội địa và một số tuyến quốc tế như Thái Lan, Singapore, Hàn Quốc… và Cảng hàng không quốc tế Đà Nẵng hiện nay vẫn là cảng hàng không quan trọng của cả miền Trung - Tây Nguyên.\r\n\r\nKinh nghiệm du', '0000-00-00', 'Nguyễn Quang Tùng', 'nui-than-tai-1.jpg'),
(2, 'Kinh nghiệm du lịch bụi Sapa ai cũng phải nhớ để có chuyến đi vui vẻ', 'Du lịch sapa chủ yếu dựa vào tài nguyên khí hậu mát mẻ và du lịch văn hóa, là nơi có nhiều dân tộc cùng sinh sống. Trong đó có hai dân tộc thiểu số nổi bật là Mông và Dao. Đến Sapa bạn sẽ được thư giãn trong bầu không khí mát lạnh của xứ sở cận ôn đới, đi', '2020-07-14', 'Nguyễn Duy Tuấn', '1499744020-sapa-vyc1.jpg'),
(3, 'Những bãi biển tuyệt nhất Việt Nam và ai cũng có thể đến trải nghiệm', 'Phú Quốc với nhiều bãi biển hoang sơ trong lành, cát trắng biển xanh điển hình như bãi Trường, nhiều du khách ví von Bãi Trường như một tấm lụa trắng khoác hờ trên vai người thiếu nữ. Bãi Kem lãng mạn như một bức tranh đẹp của tạo hóa với biển xanh, cát t', '2020-07-15', 'Nguyễn Văn Thoan', 'kelingking-secret-point-beach-04154.jpg'),
(4, 'Kinh nghiệm du lịch Đà Nẵng Ngắn ngày cho gia đình và cá nhân', '<p>Trước hết Vietnam Tourist xin gửi lời ch&agrave;o tr&acirc;n trọng v&agrave; lời cảm ơn tới qu&yacute; kh&aacute;ch h&agrave;ng gần xa đ&atilde; gh&eacute; thăm Website, sử dụng dịch vụ&nbsp;<strong>du lịch</strong>&nbsp;của ch&uacute;ng t&ocirc;i tron', '0000-00-00', '65465465', 'kelingking-secret-point-beach-04154.jpg');

