INSERT INTO `tbl_tour` (`id_tour`, `id_destination`, `id_activities`, `Code`, `Name_tour`, `startDate`, `time_tour`, `Price`, `Img`, `Img1`, `Img2`, `Img3`, `Sale`, `Content`, `CtTour`, `Feature`) VALUES
(9, 3, 14, '69-tT', 'Đà Nẵng - Cù Lao Chàm 1 Ngày', '2020-07-09', '1 ngày', '520.000', '1563184889_cu-lao-cham-1.jpg', '1563184889_cu-lao-cham-2.jpg', '1563184889_cu-lao-cham-4.jpg', 'danang.jpg', '', 'Cù Lao Chàm là một cụm đảo thuộc xã đảo Tân Hiệp, thành phố Hội An, tỉnh Quảng Nam, cách bờ biển Cửa Đại 15km. Đây là khu vực được UNESCO công nhận là Khu dự trữ sinh quyển thế giới vào năm 2009. Du lịch Cù Lao Chàm mấy năm trở lại đây thu hút khá nhiều d', ' <p >\r\n                <span >07h30: Xe du lịch và HDV LỮ HÀNH VIỆT sẽ đón du khách tại Đà Nẵng hoặc Hội An, Xe sẽ đưa du khách đến tại bến cảng du lịch Cửa Đại để lên Ca Nô chuẩn bị khởi hành đi tham quan Đảo Cù Lao Chàm.</span>\r\n            </p>\r\n            <p >\r\n                <span >08h30: Hoàn tất thủ tục xuất bến Cửa Đại. Ca nô cao tốc đời mới xuất bến và đưa quí khách vượt cửa biển Cửa Đại. Trên ca nô quý khách có thể ngắm toàn cảnh biển và trải nghiệm cảm giác mạnh với Ca Nô Cao Tốc để đến đảo Cù Lao Chàm bắt đầu cuộc hành trình khám phá du lịch Cù Lao Chàm. ( Mất 15 – 20 phút ngồi ca nô cao tốc tuỳ vào tình hình thời tiết).</span>\r\n            </p>\r\n            <p >\r\n                <span >09h00: Hành trình tham quan du lịch Cù Lao Chàm chính thức bắt đầu: Hướng dẫn viên sẽ đưa du khách tham quan các điểm nổi bật trên đảo như: Bảo tàng sinh vật biển, Bảo tồn Cù Lao Chàm, Âu thuyền: Nơi tránh bão của thuyền bè ngư dân trên đảo, và điều đặc biệt là chùa Hải Tạng có niên đại trên 300 năm. Ngoài ra quí khách còn chiêm ngưỡng giếng cổ được người Chăm xây dựng cách đây hàng trăm năm còn lại trên đảo. Và không thể bỏ qua CHỢ HẢI SẢN: Nơi giao lưu và buôn bán các loại Hải sản mà ngư dân đánh bắt sau 1 đêm tươi ngon và giá thật sự bình dân</span>\r\n            </p>\r\n            <p >\r\n                <span >11h00: CA NÔ CAO TỐC ĐỜI MỚI đón quý khách sang bãi CHỒNG - bãi biển sạch đẹp nhất tại Cù Lao Chàm. Tại đây có 2 nhà vệ sinh sạch đẹp mới được xây dựng để quý khách thay đồ chuẩn bị tư trang để lên ca nô tham gia lặn biển ngắm San hô và ngắm các sinh vật biển mà duy nhất chỉ có tại vùng đảo Cù Lao Chàm.</span>\r\n            </p>\r\n            <p >\r\n                <span >11h15: Ca nô đưa quý khách đi lặn ngắm san hô tại Cù Lao Chàm. Quý khách sẽ mặc áo phao và kính lặn và được HDV đưa đi ngắm san hô.</span>\r\n            </p>\r\n            <p >\r\n                <span >12h30: Quý khách quay về bãi Chồng tắm nước ngọt, thay đồ- cùng thưởng thức các món ăn đặc sản tại nhà hàng Cù Lao Chàm: Cùng tận hưởng các loại đặc sản hải sản. và đặc biệt là rau rừng mắm nêm một đặc sản của Đảo Cù Lao Chàm. Tận hưởng những cảm giác thú vị của mùa hè, được thảo thích đùa vui cùng sóng biển, tắm nắng trên đảo</span>\r\n            </p>\r\n            <p >\r\n                <span >14h20: Quý khách tập trung lên tàu về lại đất liền. Xe ô tô sẽ đưa qúi khách về Đà Nẵng (Nếu du khách có nhu cầu tham quan Hội An, xe du lịch Miền Trung chúng tôi sẽ đưa quí khách vào Hội An). Cảm ơn và trả quý khách tại điểm đón.</span>\r\n            </p>', 'On'),
(10, 3, 14, '94-tT', 'Đà Nẵng - Bà Nà - Sơn Trà - Ngũ Hành Sơn - Hội An 3 Ngày', '2020-07-04', '3 Ngày 2 Đêm', '1990.000', 'danang_1543552135.jpeg', '1575619993_chua-cau-hoi-an-1.jpg', '1575619993_ngu-hanh-son-da-nang.jpg', 'danang.jpg', '', 'Nhắc tới du lịch Đà Nẵng là nhắc tới một thành phố biển với bãi biển dài hơn 60 km. Với bãi biển đẹp, trải dài thoai thoải và cát trắng miên man, biển Đà Nẵng được tạp chí Forbes của Mỹ bình chọn là 1 trong 6 bãi biển quyến rũ nhất hành tinh.', ' <p >\r\n                <span >NGÀY 1: SB ĐÀ NẴNG - NGŨ HÀNH SƠN - HỘI AN (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Xe &amp; HDV đón quý khách tại&nbsp; sân bay Đà Nẵng ( chuyến bay từ 08h00 – 12h00) đưa Quý khách&nbsp; đi dùng bữa trưa&nbsp; ở nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Nhận phòng K/sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: 15h30: Xe và HDV đón khách tại khách sạn,</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn ghé thăm&nbsp; Làng nghề điêu khắc đá Non Nước, chiêm ngưỡng các tác phẩm nghệ thuật bằng đá tinh xảo, sống động được ra đời qua đôi bàn tay khéo léo của những nghệ nhân giàu kinh nghiệm. Chinh phục Ngũ Hành Sơn, khám phá vẻ đẹp huyền ảo của các hang động và viếng thăm 2 ngôi chùa cổ hàng trăm năm tuổi Linh Ứng, Tam Thai. Quý khách còn có thể thu cả trời biển núi sông Đà Nẵng vào tầm mắt, cảm nhận hơi thở tươi mát từ những cơn gió mang theo chút vị mặn mòi của biển cả từ Vọng Hải Đài và Vọng Giang Đài.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn tiếp tục khởi hành đến Di sản văn hóa UNESCO - Phố cổ Hội An.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý khách thưởng thức bữa tối đặc sản tại Phố cổ: Cao lầu, bánh bao, bánh vạc, hoành thánh,...</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn bách bộ tham quan mua sắm tại Phố cổ. Quý khách có thể chiêm ngưỡng một Hội An lung linh sắc đèn lồng trong đêm, ghé thăm các địa điểm du lịch nổi tiếng như Hội quán Phúc Kiến, chùa Cầu, nhà cổ Phùng Hưng, Làng nghề thủ công mỹ nghệ, mua vài món quà lưu niệm nhỏ độc đáo của Hội An và tận hưởng bầu không khí thanh bình nơi Phố cổ.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn khởi hành về lại thành phố Đà Nẵng, về khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: ĐÀ NẴNG - BÀ NÀ (ĂN SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Điểm tâm buffet tại khách sạn. Quý Khách khởi hành đi Bà Nà ( Tự túc chi phí vé cáp treo theo giá của Sungroup) nơi mà quý khách khám phá những khoảnh khắc giao mùa bất ngờ Xuân – Hạ - Thu – Đông trong 1 ngày.&nbsp; Ngồi cáp treo dài nhất thế giới (gần 6.000m), tham quan Đồi Vọng Nguyệt, chùa Linh Ứng, Thích Ca Phật Đài, khu chuồng ngựa cũ của Pháp, vườn tịnh tâm và đỉnh nhà rông. Khám phá Cây Cầu Vàng vừa độc vừa lạ.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Ăn trưa Buffet tại Bà Nà. Tiếp tục tham quan và tự do vui chơi tại Bà Nà.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe đưa Quý Khách khởi hành về lại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Ăn tối nhà hàng. Quý khách chiêm ngưỡng vẻ đẹp Đà Nẵng từ trên cao khi tham quan Vòng Quay Mặt Trời (Chi phí tự túc) hoặc xem Cầu Rồng phun Lửa và Nước (Thứ bảy hoặc chủ nhật hàng tuần)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: ĐÀ NẴNG - SƠN TRÀ - TIỄN KHÁCH&nbsp; (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Điểm tâm tại khách sạn.&nbsp; Xe va Hướng dẫn viên đưa Quý Khách Khởi hành đi BÁN ĐẢO SƠN TRÀ cách trung tâm Tp Đà Nẵng 10km về hướng Đông Bắc, là bức bình phong che chắn bão gió cho Thành phố Đà Nẵng. Quý khách Viếng chùa Linh Ứng, chiêm ngưỡng Tượng Phật Quan Thế Âm Bồ Tát cao nhất Việt Nam 67m. Xe và Hướng dẫn sẽ đưa Qúy khách dạo quanh, ngắm nhìn và nghe hướng dẫn thuyết minh về những điểm nhấn kiến trúc nổi tiếng của Thành Phố Du Lịch Đà Nẵng:&nbsp; Tòa nhà hành chính, Cầu Quay sông Hàn, Cầu rồng: tản bộ hít thở không khí trong lành bên bờ sông Hàn, tham quan và chụp hình lưu lại dấu ấn khó quên tại Cầu Tình yêu, chiêm ngưỡng tượng Cá chép hóa rồng&nbsp; …Mua sắm quà tại siêu thị đặc sản miền Trung hoặc chợ Hàn.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Ăn trưa nhà hàng với đặc sản nổi tiếng Đà Nẵng “Bánh tráng thịt heo 2 đầu da &amp; Mỳ Quảng”.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe tiễn khách ra Sân bay/ Ga Đà Nẵng ( chuyến bay từ 14h00 – 17h00)&nbsp; Sau thời gian&nbsp; trên quý khách vui lòng tự túc ra sân bay</span>\r\n            </p>\r\n            <p >\r\n                <span >HDV chào tạm biệt du khách, kết thúc chương trình tham quan thú vị tại đây.</span>\r\n            </p>', 'On'),
(11, 3, 15, '1-tT', 'Đà Nẵng - Công Viên Suối Khoáng Nóng Thần Tài ', '2020-07-04', '1 Ngày', '250.000', '1563183137_cong-vien-suoi-khoang-nong-than-tai-2.jpg', '1563183137_cong-vien-suoi-khoang-nong-than-tai-1.jpg', '1563183137_cong-vien-suoi-khoang-nong-than-tai-3.jpg', 'danang.jpg', '', 'Nhắc tới du lịch Đà Nẵng là nhắc tới một thành phố biển với bãi biển dài hơn 60 km. Với bãi biển đẹp, trải dài thoai thoải và cát trắng miên man, biển Đà Nẵng được tạp chí Forbes của Mỹ bình chọn là 1 trong 6 bãi biển quyến rũ nhất hành tinh.', ' <p >\r\n                <span >08h00-08h30: Xe và HDV Lữ Hành Việt đón quý khách tại điểm hẹn khởi hành đến khu tắm khoáng Núi Thần Tài (cách trung tâm thành phố Đà Nẵng 35 km).</span>\r\n            </p>\r\n            <p >\r\n                <span >09h00: Dừng chân tại cổng khu du lịch, đoàn được hướng dẫn đưa vào quầy lễ tân nhận vé, sắp xếp gửi tư trang và chuẩn bị khởi động cho một cuộc vui chơi, nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >09h30: Xe điện đưa đoàn di chuyển đến khu vực “bọng rồng” với hồ bơi lớn cùng các trò chơi nước dành cho trẻ em và người lớn. Tại đây, quý khách tự do tắm khoáng tại khu vực tắm khoáng tập thể, ngoài ra có thể thêm dịch vụ tắm bùn, tắm onsen, tắm trà, sữa, rượu, cà phê (Chi phí tự túc). Quý khách tự tay luộc trứng tại khu suối khoáng nóng, tham gia các trò chơi dưới nước như: dòng sông lười Động Long Tiên, khu hồ bơi và khu vui chơi ngoài trời,....</span>\r\n            </p>\r\n            <p >\r\n                <span >11h30: Dùng bữa trưa với BUFFET - ẨM THỰC VIỆT (Hoặc Set Menu)&nbsp; và tự do nghỉ ngơi tại nhà hàng Rồng Đỏ trong khu du lịch.</span>\r\n            </p>\r\n            <p >\r\n                <span >14h00: HDV tập trung đoàn tiếp tục hành trình khám phá thiên nhiên Núi Thần Tài. Quý khách có cơ hội được tham quan các hạng mục như: Miếu Sơn Thần, bồn tắm tự nhiên bằng đá lớn nhất Việt Nam, Vườn thực vật phong phú như: Vườn Chôm chôm, vườn vải, vườn xoài,...</span>\r\n            </p>\r\n            <p >\r\n                <span >15h30: Tạm biệt Núi Thần Tài, quý khách khởi hành trở về Đà Nẵng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chào tạm biệt và kết thúc chương trình!</span>\r\n            </p>', 'Off'),
(12, 3, 15, '253-tT', 'Đà Nẵng - Bà Nà - Sơn Trà - Hội An - Cù Lao Chàm', '2020-07-05', '3 Ngày 2 Đêm', '3190.000 ', 'danang_1543552135.jpeg', '1563248499_ba-na-hills.jpg', '1563184889_cu-lao-cham-4.jpg', '1563184889_cu-lao-cham-1.jpg', '2.550.000', 'Nhắc tới du lịch Đà Nẵng là nhắc tới một thành phố biển với bãi biển dài hơn 60 km. Với bãi biển đẹp, trải dài thoai thoải và cát trắng miên man, biển Đà Nẵng được tạp chí Forbes của Mỹ bình chọn là 1 trong 6 bãi biển quyến rũ nhất hành tinh.', '  <p >\r\n                <span >NGÀY 1: SB ĐÀ NẴNG - BÀ NÀ - MỸ KHÊ (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Xe và HDV Cty Lữ Hành Việt đón khách tại điểm hẹn trung tâm / sân bay TP Đà Nẵng. (Qúy khách nào chuyến bay tới trễ hoặc delay sau 8:00; vui lòng tự túc lên Bà Nà&nbsp; nhập đoàn)</span>\r\n            </p>\r\n            <p >\r\n                <span >Khởi hành đi khu du lịch Bà Nà Hills (Tự túc vé cáp treo theo giá quy định của SunGroup),&nbsp; nơi quý khách có thể khám phá những khoảnh khắc giao mùa bất ngờ Xuân - Hạ - Thu - Đông trong 1 ngày.&nbsp; Ngồi cáp treo dài nhất thế giới (gần 6.000m), tham quan Đồi Vọng Nguyệt, chùa Linh Ứng, Thích Ca Phật Đài, khu chuồng ngựa cũ của Pháp, vườn tịnh tâm và đỉnh nhà rông. Tiếp tục ngồi cáp treo đến đỉnh Nghinh Phong, biệt thự Lệ Nim, Lầu Vọng Nguyệt, Cầu Treo Bà Nà và chinh phục đỉnh núi Chúa ở độ cao 1.487m so với mực nước biển để thưởng thức quang cảnh núi rừng Bà Nà và toàn cảnh Đà Nẵng và Quảng Nam trên cao.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tiếp tục tham gia các trò chơi phiêu lưu mới lạ, ngộ nghĩnh, hấp dẫn, hiện đại tại khu trò chơi Fantasy Park (Vòng quay tình yêu, phi công SKIVER, đường đua lửa, cối xay gió, người nhện, tháp rơi xoay tự do, Xem phim 4D, Công viên khủng long, Bảo tàng tượng sáp… chi phí trò chơi tự túc)</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách ăn buffet tại nhà hàng. Chiều khởi hành về Đà Nẵng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tự do tắm biển tại bãi biển Mỹ Khê với bờ cát mịn trải dài, bãi biển sạch đẹp và làn nước trong xanh.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý khách dùng bữa tối tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Quý khách tự do khám phá Phố Biển Đà Nẵng về đêm với các cây cầu nổi tiếng: Cầu&nbsp; Rồng phun lửa, Cầu treo Thuận Phước, Cầu Quay sông Hàn, Trung Tâm Thương Mại, Khu phố ẩm thực, Café - Bar - Discotheque, hoặc trải nghiệm cảm giác với Vòng quay Mặt trời Sun Wheel - Top 10 vòng quay cao nhất Thế Giới... (Vé Sun Wheel tự túc). Nghỉ đêm tại khách sạn Đà Nẵng.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: ĐÀ NẴNG - CÙ LAO CHÀM - HỘI AN (ĂN SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách dùng&nbsp; điểm tâm tại khách sạn. Xe và HDV đón khách lúc 7h30. Khởi hành vào Hội An ngồi tàu Cao Tốc tại biển Cửa Đại - Hội An đi Cù Lao Chàm, khu dự trữ sinh quyển Thế giới gồm cụm đảo ngoài khơi biển Đông cách Hội An 12 hải lý. Tham quan chùa Hải Tạng hơn 400 năm tuổi, Âu thuyền – khu vực tránh bão của ngư dân, Khu dân cư &amp; các lăng miếu cổ.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Ăn trưa hải sản tại nhà hàng trên đảo.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tiếp tục thăm khu bảo tồn biển tại bãi Bắc, hòn Dài và tham gia các hoạt động lặn biển(Snokelling) khám phá san hô, cá cảnh, câu cá, bắt ốc, giăng lưới, tắm biển,... .</span>\r\n            </p>\r\n            <p >\r\n                <span >(Trường hợp biển động không đi Cù Lao Chàm, Quý khách sẽ đến với “ Thiên Đường Nghĩ Dưỡng Galina” Khu tắm bùn khoáng&nbsp; lớn nhất và dịch vụ cao cấp nhất tại Đà Nẵng.)</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Tàu đưa quý khách về lại cảng, tiếp tục vào Hội An - Được UNESCO công nhận là Di sản văn hoá thế giới với:&nbsp; + Chùa Cầu Nhật Bản + Bảo tàng văn hóa Sa Huỳnh + Nhà Cổ hàng trăm năm tuổi + Hội Quán Phước Kiến &amp; Xưởng thủ công mỹ nghệ… Tự do mua sắm các mặt hàng lưu niệm phong phú tại phố cổ.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Ăn tối nhà hang Hội An. Xe đưa quý khách về lại Đà Nẵng dọc đường biển để ngắm thành phố Đà Nẵng tuyệt đẹp về đêm với hàng loạt khu nghỉ dưỡng và Resort cao cấp. Nghỉ đêm tại khách sạn Đà Nẵng.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: ĐÀ NẴNG - SƠN TRÀ - TIỄN KHÁCH (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Điểm tâm tại khách sạn. Xe va Hướng dẫn viên đưa Quý Khách Khởi hành đi BÁN ĐẢO SƠN TRÀ cách trung tâm Tp Đà Nẵng 10km về hướng Đông Bắc, là bức bình phong che chắn bão gió cho Thành phố Đà Nẵng. Quý khách Viếng chùa Linh Ứng, chiêm ngưỡng Tượng Phật Quan Thế Âm Bồ Tát cao nhất Việt Nam 67m. Xe và Hướng dẫn sẽ đưa Qúy khách dạo quanh, ngắm nhìn và nghe hướng dẫn thuyết minh về những điểm nhấn kiến trúc nổi tiếng của Thành Phố Du Lịch Đà Nẵng:&nbsp; Tòa nhà hành chính, Cầu Quay sông Hàn, Cầu rồng: tản bộ hít thở không khí trong lành bên bờ sông Hàn, tham quan và chụp hình lưu lại dấu ấn khó quên tại Cầu Tình yêu, chiêm ngưỡng tượng Cá chép hóa rồng… Mua sắm quà tại siêu thị đặc sản miền Trung hoặc chợ Hàn.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Ăn trưa nhà hàng với đặc sản nổi tiếng Đà Nẵng “Bánh tráng thịt heo 2 đầu da &amp; Mỳ Quảng”.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe tiễn khách ra Sân bay/ Ga Đà Nẵng ( chuyến bay từ 14h00 – 17h00)&nbsp; Sau thời gian&nbsp; trên quý khách vui lòng tự túc ra sân bay</span>\r\n            </p>\r\n            <p >\r\n                <span >HDV chào tạm biệt du khách, kết thúc chương trình tham quan thú vị tại đây.</span>\r\n            </p>', 'Off'),
(13, 3, 14, '890-tT', 'Đà Nẵng - Bà Nà Hills', '2020-07-07', '1 Ngày', '550.000', '1575607406_ba-na-hills-1.jpg', '1563248499_lang-phap.jpg', '1563248499_tro-choi-o-ba-na-hills.jpg', '1563248499_ba-na-hills.jpg', '', 'Nhắc tới du lịch Đà Nẵng là nhắc tới một thành phố biển với bãi biển dài hơn 60 km. Với bãi biển đẹp, trải dài thoai thoải và cát trắng miên man, biển Đà Nẵng được tạp chí Forbes của Mỹ bình chọn là 1 trong 6 bãi biển quyến rũ nhất hành tinh.', '<p >\r\n                <span >Sáng:</span>\r\n            </p>\r\n            <p >\r\n                <span >8h00: Xe và HDV LỮ HÀNH VIỆT đến đón quý khách khởi hành chuyến tham quan đến Khu du lịch nghỉ dưỡng Bà Nà Hills -&nbsp; đường lên tiên cảnh. Đến chân núi Bà Nà, HDV làm thủ tục giúp quý khách lên Cáp treo bắt đầu hành trình lên núi bằng cáp treo đạt 2 kỷ lục của Thế Giới tuyến cáp treo một dây dài nhất thế giới và có độ cao chênh lệch giữa ga trên và ga dưới lớn nhất thế giới 1.291,81m.</span>\r\n            </p>\r\n            <p >\r\n                <span >Thả mình trong không khí se lạnh của Bà Nà, thưởng thức bốn mùa trong một ngày Bà Nà mang lại cho quý khách. Tại đây quý khách sẽ tham quan các điểm thú vị như: Hầm rượu Debay, vườn hoa hay Chùa Linh Ứng 2 của Đà Nẵng.</span>\r\n            </p>\r\n            <p >\r\n                <span >11h00: Quý khách dùng bữa trưa buffet tại Làng Pháp.</span>\r\n            </p>\r\n            <p >\r\n                <span >13h00: Quý khách tham quan Làng Pháp, nhà thờ Đá hay tự mình trải nghiệm những trò chơi thú vị trong khu vui chơi trong nhà Fantasy lớn nhất Việt Nam. Khám phá với không gian lễ hội cùng các nghệ sỹ biểu diễn nước ngoài tại Bà Nà</span>\r\n            </p>\r\n            <p >\r\n                <span >15h00: Quý khách tập trung xuống cáp để về lại Đà Nẵng. Trên đường về, HDV sẽ đưa quý khách ghé vào Trung tâm đặc sản miền Trung để quý khách tự do mua sắm.</span>\r\n            </p>\r\n            <p >\r\n                <span >17h00: Quý khách về khách sạn, nghỉ ngơi. Kết thúc chương trình. Chia tay và hẹn gặp lại Quý Khách.</span>\r\n            </p>', 'On'),
(14, 5, 15, '706-tT', ' Hà Nội - Nha Trang - Đà Lạt  + VMB & Xe Ôtô', '2020-07-08', '5 Ngày 4 Đêm', '5990.000', 'nha-trang.jpg', '1554956741_NhaTrang11.jpg', '1564135188_nha-trang-2.jpg', '1564135188_nha-trang-tour1.jpg', '', 'Mùa hè đang đến cũng là khoảng thời gian du lịch cho mọi nhà. Thường thì mọi người luôn chọn ra Biển cho những chuyến du lịch Hè. Nha Trang chính là một lựa chọn lý tưởng cho mọi người với Vinpearl Nha Trang 5 sao sang trọng, hòn Mun, Hòn Một nước trong veo và san hô lộng lẫy. Viện Hải dương học Nha Trang có trên 20.000 mẫu sinh vật dưới nước, tháp Bà Ponagar hoàn mỹ của người Chăm, cảng Vũng Rô, làng Đại Lãnh và chợ Đầm nhộn nhịp.', '            <p >\r\n                <span >NGÀY 1: HÀ NỘI - NHA TRANG (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Xe và Hướng dẫn viên (HDV) đón Quý khách tại Cổng Công Viên Thống Nhất - Đường Trần Nhân Tông - Quận Hai Bà Trưng - Tp Hà Nội, đáp chuyến bay đi Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đến sân bay Cam Ranh, xe và HDV địa phương đón đoàn về lại trung tâm thành phố ăn trưa đối với khách bay chuyến 08h45. Nếu khách bay chuyến 11h35 và 12h00, quý khách dùng bữa trưa nhẹ trên máy bay và chúng tôi sẽ hoàn lại chi phí tiền ăn là 150.000đ/ 1 khách người lớn.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau khi đến Tp Nha Trang, quý khách nhận phòng khách sạn 4 sao.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Đoàn tham quan thành phố Nha Trang với các địa danh:</span>\r\n            </p>\r\n            <p >\r\n                <span >Tháp Bà Ponagar: Một công trình kiến trúc cổ của người Chăm để lại trên đất Nha Trang. Đoàn chụp hình lưu niệm và tham quan khu di tích.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn ghé thăm khu tắm khoáng I-Resort, tậnhưởng dịch vụ tắm khoáng nóng đặc biệt, tắm bùn, ngâm khoáng thảo dược có lợi cho sức khoẻ…(Chi phí tự túc)</span>\r\n            </p>\r\n            <p >\r\n                <span >19h00: Đoàn dùng bữa tối tại nhà hàng. Khi đêm xuống, quý khách dạo bước bên phố biển, hoặc tự do thưởng thức những món ngon đường phố Nha Trang. Nghỉ đêm tại khách sạn 4*.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: KHÁM PHÁ BIỂN ĐẢO NHA TRANG (ĂN SÁNG, TRƯA, TỰ TÚC BỮA TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Ăn sáng tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >07h30: Quý khách lên xe đi Cảng Cầu Đá khám phá Vịnh Nha Trang - một trong 29 vịnh đẹp nhất thế giới. Đoàn xuống Cano bắt đầu hành trình khám phá vịnh Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Mun: Quý khách tham quan, bơi lặn trực tiếp hoặc đi tàu đáy kính, lặn bình Oxy (chi phí tự túc) ngắm nhìn những mảng san hô và các loài sinh vật biển quý hiếm tại khu Bảo tồn biển lớn nhất Việt Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Một: Quý khách thăm quan, tắm biển và chơi các trò chơi lướt sóng, cano tại đảo (Chi phí tự túc)</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Sẻ Tre: Quý khách dùng bữa trưa tại nhà hàng. Tự do nằm nghỉ tại ghế bên bờ biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Cano đưa khách cập bến tàu khu du lịch Vinpearl Land – Hòn Ngọc Việt(quý khách tự trả phí cáp treo)với các trò chơi như: Quay nhào lộn, xem phim 4D, khám phá thủy Cung…</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: 18h00 Quý khách tự do ăn tối tại khu khu lịch Vinpearland và thưởng thức các chương trình biểu diễn nhạc nước – nơi tổ chức cuộc thi hoa hậu thế giới... đặc sắc.</span>\r\n            </p>\r\n            <p >\r\n                <span >20h30: Quý khách lên cáp treo và xe đón quý khách quay trở về khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: NHA TRANG - ĐÀ LẠT (ĂN SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách ăn sáng tại khách sạn. Trả phòng, đoàn khởi hành đi Đà Lạt. Đà lạt - thành phố nằm trên cao nguyên Lâm Viên, được mệnh danh là thành phố ngàn hoa, thành phố của tình yêu và huyền thoại.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Đến Đà Lạt, đoàn ăn trưa tại nhà hàng, nhận phòng khách sạn 3 sao.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Quý khách tham quan nhà thờ Con Gà, nhà thờ Domain, Biệt điện Bảo Đại, tự do tham quan chụp ảnh.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý kháchăn tối tại nhà hàng. Quý khách tự do khám phá Đà Lạt, thưởng thức hương vị café Đà lạt, dạo chợ mua sắm, thưởng thức sữa đậu nành nóng, khoai nướng, … hoặc đi xe đạp, xe ngưa dạo quanh Hồ Xuân Hương. Nghỉ đêm tại khách sạn 3*.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 4: THAM QUAN TP ĐÀ LẠT (ĂN SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách ăn sáng tại khách sạn. Trả phòng khách sạn. Xe và hướng dẫn đưa đoàn tham quan Trúc Lâm Thiền viện, ngắm cảnh Hồ Tuyền Lâm với Núi Voi, thác Datanla (Quý khách có thể đi bằng cáp treo qua đồi Robin ngắm toàn cảnh thành phố Đà lạt hoặc thử cảm giác mạnh với trò chơi máng trượt và khám phá Thác Tử thần&nbsp; - chi phí tự túc).</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách ăn trưa tại nhà hàng Datanla.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Đoàn tiếp tục tham quan ga xe lửa Đà Lạt với vẻ đẹp kiến trúc độc đáo mang đậm kiến trúc Pháp và những đoàn tàu cổ kính ghi dấu thời gian, nhà ga cổ nhất của Việt Nam. Quý khách tham quan tiếp chùa Linh Phước -ngôi chùa còn có tên gọi khác là chùa “ve chai” bởi trong sân chùa có con rồng dài 49 mét được trang trí bề mặt bằng cả chục ngàn vỏ chai. Đây là công trình kiến trúc khảm sành đặc sắc của thành phố Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn về lại thành phố, quý khách dùng bữa tối.Nghỉ đêm tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 5: ĐÀ LẠT - HÀ NỘI (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Ăn sáng và trả phòng khách sạn. Xe và hướng dẫn đưa đoàn tham quan: Quảng trường Lâm Viên không gian rộng lớn, thoáng mát ấn tượng với công trình nghệ thuật khổng lồ; Trường Cao Đẳng Sư Phạm được công nhận là một trong số 1.000 công trình xây dựng độc đáo của thế giới trong thế kỷ 20; Showrooom hoa khô Đà Lạt - quý khách sẽ được chiêm ngưỡng những mẫu hoa tươi được sấy khô theo công nghệ Nhật Bản. Quý khách đến Lò mứt, chợ Đà Lạt mua sắm đặc sản, quà lưu niệm cho người thân.</span>\r\n            </p>\r\n            <p >\r\n                <span >11h30: Đoàn ăn trưa tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau bữa trưa, xe đưa đoàn ra sân bay Liên Khương đáp chuyến bay về Hà Nội</span>\r\n            </p>\r\n            <p >\r\n                <span >16h35: Đến sân bay Nội Bài, xe và hướng dẫn viên đưa đoàn về điểm đón ban đầu. Kết thúc chương trình. Hẹn gặp lại quý khách.</span>\r\n            </p>', 'On'),
(15, 5, 15, '159-tT', ' Hà Nội - Đà Lạt - Nha Trang Du Lịch Hè 2020 ', '2020-08-07', '4 ngày 3 đêm', '6690.000', '1554956741_NhaTrang11.jpg', '1564135188_nha-trang-2.jpg', '1564135188_du-lich-da-lat.jpg', 'gioi-thieu-ve-ha-noi-1.jpg', '', 'Mùa hè đang đến cũng là khoảng thời gian du lịch cho mọi nhà. Thường thì mọi người luôn chọn ra Biển cho những chuyến du lịch Hè. Nha Trang chính là một lựa chọn lý tưởng cho mọi người với Vinpearl Nha Trang 5 sao sang trọng, hòn Mun, Hòn Một nước trong veo và san hô lộng lẫy. Viện Hải dương học Nha Trang có trên 20.000 mẫu sinh vật dưới nước, tháp Bà Ponagar hoàn mỹ của người Chăm, cảng Vũng Rô, làng Đại Lãnh và chợ Đầm nhộn nhịp.', ' <p >\r\n                <span >NGÀY 1: HÀ NỘI - NHA TRANG ĂN TỐI (ĂN TRƯA TRÊN MÁY BAY)</span>\r\n            </p>\r\n            <p >\r\n                <span >09h30: Xe và Hướng dẫn viên (HDV) đón Quý khách tại Cổng Công Viên Thống Nhất – Đường Trần Nhân Tông – Quận Hai Bà Trưng – Tp Hà Nội, đáp chuyến bay VN1575 lúc 12h00 đi Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >14h00: Đến sân bay Cam Ranh, xe và HDV địa phương đón đoàn về lại trung tâm thành phố nhận phòng khách sạn 4*.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Đoàn tham quan thành phố Nha Trang với các địa danh:</span>\r\n            </p>\r\n            <p >\r\n                <span >Tháp Bà Ponagar: Một công trình kiến trúc cổ của người Chăm để lại trên đất Nha Trang. Đoàn chụp hình lưu niệm và thăm quan khu di tích.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn ghé thăm khu tắm khoáng I-Resort, tận hưởng dịch vụ tắm khoáng nóng đặc biệt, tắm bùn, ngâm khoáng thảo dược có lợi cho sức khoẻ…(Chi phí tự túc)</span>\r\n            </p>\r\n            <p >\r\n                <span >19h00: Đoàn dùng bữa tối tại nhà hàng. Khi đêm xuống, quý khách dạo bước bên phố biển, hoặc tự do thưởng thức những món ngon đường phố Nha Trang. Nghỉ đêm tại khách sạn 4*.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: KHÁM PHÁ BIỂN ĐẢO NHA TRANG (ĂN SÁNG, TRƯA, TỰ TÚC BỮA TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Ăn sáng tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >07h30: Quý khách lên xe đi Cảng Cầu Đá khám phá Vịnh Nha Trang - một trong 29 vịnh đẹp nhất thế giới. Đoàn xuống Cano bắt đầu hành trình khám phá vịnh Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Mun: Quý khách tham quan, bơi lặn trực tiếp hoặc đi tàu đáy kính, lặn bình Oxy (chi phí tự túc) ngắm nhìn những mảng san hô và các loài sinh vật biển quý hiếm tại khu Bảo tồn biển lớn nhất Việt Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Một: Quý khách thăm quan, tắm biển và chơi các trò chơi lướt sóng, cano tại đảo (Chi phí tự túc)</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Sẻ Tre: Quý khách dùng bữa trưa tại nhà hàng. Tự do nằm nghỉ tại ghế bên bờ biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Cano đưa kháchcập bến tàu khu du lịch Vinpearl Land – Hòn Ngọc Việt (quý khách tự trả phí cáp treo)với các trò chơi như: Quay nhào lộn, xem phim 4D, khám phá thủy Cung…</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: 18h00 Quý khách tự do ăn tối tại khu khu lịch Vinpearland và thưởng thức các chương trình biểu diễn nhạc nước – nơi tổ chức cuộc thi hoa hậu thế giới... đặc sắc.</span>\r\n            </p>\r\n            <p >\r\n                <span >20h30: Quý khách lên cáp treo và xe đón quý khách quay trở về khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: NHA TRANG - ĐÀ LẠT (ĂN SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách ăn sáng tại khách sạn. Trả phòng, đoàn khởi hành đi Đà Lạt. Đà Lạt – thành phố nằm trên cao nguyên Lâm Viên, được mệnh danh là thành phố ngàn hoa, thành phố của tình yêu và huyền thoại.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Đến Đà Lạt, đoàn ăn trưa tại nhà hàng, nhận phòng khách sạn 3 sao.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Quý khách tham quan nhà thờ Con Gà, nhà thờ Domain, Biệt điện Bảo Đại, tự do tham quan chụp ảnh.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý kháchăn tối tại nhà hàng. Quý khách tự do khám phá Đà lạt, thưởng thức hương vị café Đà lạt, dạo chợ mua sắm, thưởng thức sữa đậu nành nóng, khoai nướng, … hoặc đi xe đạp, xe ngưa dạo quanh Hồ Xuân Hương. Nghỉ đêm tại khách sạn 3*.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 4: ĐÀ LẠT – HÀ NỘI (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách ăn sáng tại khách sạn. Trả phòng khách sạn. Xe và hướng dẫn đưa đoàn tham quan Trúc Lâm Thiền Viện, ngắm cảnh Hồ Tuyền Lâm với Núi Voi, thác Datanla (Quý khách có thể đi bằng cáp treo qua đồi Robin ngắm toàn cảnh thành phố Đà lạt hoặc thử cảm giác mạnh với trò chơi máng trượt và khám phá Thác Tử thần - chi phí tự túc).</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách ăn trưa tại nhà hàng Datanla.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe đưa đoàn ra sân bay Liên Khương đáp chuyến bay VN1576 về Hà Nội lúc 14h40.</span>\r\n            </p>\r\n            <p >\r\n                <span >16h35: Đến sân bay Nội Bài, xe và hướng dẫn viên đưa đoàn về điểm đón ban đầu. Kết thúc chương trình. Hẹn gặp lại quý khách.</span>\r\n            </p>', 'Off'),
(16, 5, 15, '198-tT', ' Hà Nội - Cam Ranh - Ninh Thuận - Nha Trang  + Vé Máy Bay', '2020-07-08', '4 ngày 3 đêm', '4990.000 ', '1554956741_nha-trang-du-lich.jpg', '1554956741_nhatrang-map.jpg.jpg', 'gioi-thieu-ve-ha-noi-1.jpg', '1552038296_nha-trang-2.jpg', '', 'Nha Trang biển xanh, cát trắng, nắng vàng - nơi có vô vàn hòn đảo xinh đẹp, có khu vui chơi Vinpearl Land nổi tiếng cả nước với nhiều trò chơi hấp dẫn. Ẩm thực Nha Trang phong phú và hấp dẫn với nhiều món ăn lạ nem nướng, bún cá,.... Đặc biệt không thể không trải nghiệm tắm bùn khoáng Nha Trang nổi tiếng cả nước. Đặt tour  Nha Trang Ninh Thuận 4 ngày ngay hôm nay để cùng chúng tôi trải nghiệm những điều tuyệt vời nhất thiên đường biển xinh đẹp này!', ' <p >\r\n                <span >NGÀY 1: HÀ NỘI - CAM RANH - NINH THUẬN (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Xe ô tô và hướng dẫn viên đón quý khách tại Cổng Công Viên Thống Nhất – Đường Trần Nhân Tông – Q. Hai Bà Trưng – Tp Hà Nội, đi sân bay Nội Bài, đáp chuyến bay VN1561 lúc 11h35 hoặc chuyến bay VN1563 lúc 12h00 đi sân bay Cam Ranh.Quý khách dùng bữa trưa nhẹ trên máy bay.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau 2 tiếng bay, quý khách đến sân bay Cam Ranh, xe và HDV địa phương đón đoàn đi Ninh Thuận. Trên đường quý khách dùng bữa trưa tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Đến Ninh Thuận (Cách sân bay Cam Ranh 70km), quý khách nhận phòng Resort - Khách sạn 4*, nghỉ ngơi.Quý khách tự do dạo chơi, tắm biển Ninh Chữ.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý khách dùng bữa tối tại nhà hàng, thưởng thức hải sản biển Ninh Chữ.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: KHÁM PHÁ NINH THUẬN (ĂN SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Ăn sáng tại khách sạn. Xe đưa đoàn đi thăm quan:</span>\r\n            </p>\r\n            <p >\r\n                <span >Làng Gốm Bàu Trúc: Ngôi làng gốm cổ nhất của Đông Nam Á. Với kỹ thuật làm gốm độc đáo, đặc sắc bậc nhất Việt Nam khi không dùng bàn xoay mà dùng hoàn toàn bằng đôi tay của người thợ Chăm. Từ nhào đất, tạo hình, vẽ hoa văn…Gốm Bàu Trúc như mang cả 1 bảo tang Ninh Thuận vào trong từng sản phẩm.</span>\r\n            </p>\r\n            <p >\r\n                <span >Làng dệt Mỹ Nghiệp: Ngôi làng dệt Chăm cổ xưa nhất của Ninh Thuận.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn về lại Tp Phan Rang – Tháp Chàm dùng bữa trưa tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Quý khách lên xe tham quan:</span>\r\n            </p>\r\n            <p >\r\n                <span >Đồng Cừu An Hòa. Cùng khám phá cuộc sống du mục của đồng bào Chăm nơi đây.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tháp Poklong Garai: Đây là 1 công trình kiến trúc của đồng bào Chăm, còn sót lại tại Ninh Thuận. Một trong những cụm tháp Chăm được đánh giá là đẹp nhất tại Việt Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >Quý khách về lại khách sạn, nghỉ ngơi, tắm biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >19h00: Đoàn dùng bữa tối tại nhà hàng. Tự do khám phá thành phố Phan Rang - Tháp Chàm về đêm. Nghỉ đêm tại Resort – Khách sạn 4*.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: NINH THUẬN - NHA TRANG (ĂN SÁNG, TRƯA, ĂN TỐI TỰ TÚC)</span>\r\n            </p>\r\n            <p >\r\n                <span >07h00: Sau khi ăn sáng, quý khách trả phòng đi Nha Trang. Trên đường quý khách dừng chân thăm quan.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hang Rái: Hang Rái là một quần thể địa chất, sắp xếp tài tình của hàng trăm viên đá lớn nhỏ khác nhau, tạo nên những hình thù đẹp mắt, những hang luồn chỉ đủ một người chui qua hay những vách đá cheo leo thử thách sự can đảm của bạn. Khám phá một vòng công viên đá, bạn sẽ được chiêm ngưỡng sự hùng vĩ của biển và núi, ngắm toàn cảnh trên cao bãi san hô cổ.</span>\r\n            </p>\r\n            <p >\r\n                <span >Vườn nho: Dọc đường từ Ninh Thuận đi Vĩnh Hy, quý khách có dịp ghé thăm các vườn nho, thưởng thức siro nho, rượu nho và ăn trực tiếp nho tại vườn. Quý khách mua các sản phầm quà tặng từ nho về làm quà cho người thân và gia đình.</span>\r\n            </p>\r\n            <p >\r\n                <span >Vịnh Vĩnh Hy: Vĩnh Hy là vịnh biển nhỏ, nằm dưới chân núi Chúa cao 1040m so với mực nước biển. Quý khách thăm làng chài, lên tàu đáy kính ngắm san hô trên vịnh Vĩnh Hy.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn dùng bữa trưa tại nhà hàng Vĩnh Hy, thưởng thức hải sản biển Ninh Thuận.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn tiếp tục lên đường về Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đến Tp Nha Trang, quý khách nhận phòng khách sạn 4* và nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >15h00: Xe đón đoàn đi tham quan khu giải trí Vinpearlland (chi phí tự túc) quý khách tham gia các trò chơi:</span>\r\n            </p>\r\n            <p >\r\n                <span >Trò chơi cảm giác mạnh: Quay nhào lộn, đu quay ngựa gỗ, đu quay voi</span>\r\n            </p>\r\n            <p >\r\n                <span >Trò chơi tĩnh như: tàu lượn, đua xe, khám phá vũ trụ, trượt tuyết, lướt sóng, xe điện đụng</span>\r\n            </p>\r\n            <p >\r\n                <span >Tự do khám phá cảm giác công nghệ chiếu phim 4D mới lạ, đặc sắc</span>\r\n            </p>\r\n            <p >\r\n                <span >Khám phá thủy cung Vinpeal - với những sinh vật biển đa dạng phong phú</span>\r\n            </p>\r\n            <p >\r\n                <span >Thưởng thức cung trình biểu diễn nhạc nước vô cùng thú vị - sân khấu nơi từng diễn ra các hoạt đông văn hóa lớn: Hoa Hậu Báo Tiền Phong, Duyên dáng Việt Nam …</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý khách ăn tối tự túc tại Vinpearlland. 21:00 Quý khách đi cáp treo trở về thành phố. Xe đón đoàn đưa Qúy khách về khách sạn. Nghỉ đêm tại khách sạn ở Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 4: KHÁM PHÁ NHA TRANG (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Ăn sáng và trả phòng khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >07h30: Quý khách lên xe đi Cảng Cầu Đá khám phá Vịnh Nha Trang - một trong 29 vịnh đẹp nhất thế giới. Đoàn xuống Cano bắt đầu hành trình khám phá vịnh Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Mun: Quý khách tham quan, bơi lặn trực tiếp hoặc đi tàu đáy kính, lặn bình Oxy (chi phí tự túc) ngắm nhìn những mảng san hô và các loài sinh vật biển quý hiếm tại khu Bảo tồn biển lớn nhất Việt Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Một: Quý khách thăm quan, tắm biển và chơi các trò chơi lướt sóng, cano tại đảo (Chi phí tự túc)</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Sẻ Tre: Quý khách dùng bữa trưa tại nhà hàng. Tự do nằm nghỉ tại ghế bên bờ biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Cano đưa quý khách về lại cảng Cầu Đá. Xe đón đoàn đi mua sắm hàng hóa, đặc sản Nha Trang về làm quà cho người thân và gia đình.</span>\r\n            </p>\r\n            <p >\r\n                <span >16h00: Đoàn khởi hành ra sân bay Cam Ranh, đáp chuyến bay VN1564 lúc 19h10 về lại Hà Nội. Đoàn dùng bữa tối nhẹ trên máy bay.</span>\r\n            </p>\r\n            <p >\r\n                <span >21h10: Đến sân bay Nội Bài, xe và HDV đón đoàn về lại trung tâm Hà Nội, trả khách tại điểm đón, chia tay quý khách và hẹn gặp lại.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>', 'Off'),
(17, 5, 15, '65-tT', ' Sài Gòn - Nha Trang  - Trải Nghiệm Xe lửa Golden Train Cao Cấp & Nghỉ Vinpearl 5 Sao', '2020-07-09', '3 Ngày 4 Đêm', '6900.000 ', '1552038296_nha-trang-4.jpg', '1553241755_nha-trang-37.jpg', '1552038296_nha-trang-2.jpg', '1552038296_nha-trang-1.jpg', '', 'Nha Trang biển xanh, cát trắng, nắng vàng - nơi có vô vàn hòn đảo xinh đẹp, có khu vui chơi Vinpearl Land nổi tiếng cả nước với nhiều trò chơi hấp dẫn. Ẩm thực Nha Trang phong phú và hấp dẫn với nhiều món ăn lạ nem nướng, bún cá,.... Đặc biệt không thể không trải nghiệm tắm bùn khoáng Nha Trang nổi tiếng cả nước. Đặt tour  Nha Trang Ninh Thuận 4 ngày ngay hôm nay để cùng chúng tôi trải nghiệm những điều tuyệt vời nhất thiên đường biển xinh đẹp này!', ' <p >\r\n                <span >ĐÊM 01: TP.HCM - NHA TRANG (NGỦ ĐÊM TRÊN TÀU GOLDEN TRAIN 5 SAO)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Quý khách có mặt tại Ga Sài Gòn. HDV đón khách tại ga và làm thủ tục</span>\r\n            </p>\r\n            <p >\r\n                <span >cho khách khởi hành đi du lịch Nha Trang bằng tàu cao cấp Golden Train 5* khoang 4 - SNT2 chuyến 20h30. Khách nghỉ đêm trên tàu.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 01: NHA TRANG - VINPEARL RESORT 5 SAO (ĂN: TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách tới Ga Nha Trang lúc 06h00. Xe và HDV đón quý khách</span>\r\n            </p>\r\n            <p >\r\n                <span >tại Ga Nha Trang, xe đưa khách tới cảng Vinpearl, du khách đi cano hoặc cáp treo ra khu du lịch Vinpearl Land (Hòn Ngọc Việt).</span>\r\n            </p>\r\n            <p >\r\n                <span >Quý khách tự do vui chơi tại khu du lịch hoặc tham quan thế giới giải trí Vinpearl Land và khu thủy cung Vinpearl.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiêm ngưỡng một trong những thế giới thủy cung Vinpearl lớn ở Đông Nam Á với hàng trăm ngàn sinh vật biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tham gia các trò chơi cảm giác mạnh (quay nhào lộn, đu quay ngựa gỗ, đu quay voi), các trò chơi tĩnh (tàu lượn, đua xe, khám phá vũ trụ, trượt tuyết, lướt sóng, xe điện đụng). Đặc biệt có phòng chiếu phim 4D mới lạ, tăng thêm hiệu quả nhờ các vòi phun gió, nước để thám hiểm đại dương, các đường hầm bí hiểm hay lâu đài ma quái.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sử dụng một trong những hồ bơi lớn khu vực Đông Nam Á và bãi biển riêng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tham gia các hoạt động thể thao hằng ngày: aerobic, yoga, bóng chuyền bãi biển, bóng bàn…và phòng tập thể hình miễn phí.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sử dụng câu lạc bộ dành cho trẻ em tại tòa nhà Deluxe.</span>\r\n            </p>\r\n            <p >\r\n                <span >Vui đùa tại Công viên nước Vinpearl.</span>\r\n            </p>\r\n            <p >\r\n                <span >Bữa trưa và tối được phục vụ tại khách sạn. Nghỉ đêm tại Vinpearl Resort Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 02: NHA TRANG - BEST WESTERN PREMIER HAVANA NHA TRANG 5 SAO (ĂN: SÁNG)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Du khách ăn sáng và tự do vui chơi tại bãi biển. Quý khách làm thủ tục trả phòng, xe đưa du khách tới trung tâm thành phố, nhận phòng khách sạn Best Western Premier Havana Nha Trang - khách sạn 5 sao tại trung tâm thành phố Nha Trang cao 41 tầng với hơn 1.000 phòng nghỉ sang trọng. Best Western Premier Havana Nha Trang cũng là khách sạn có đường hầm nối ra biển, bãi đáp trực thăng, bể bơi, nhà hàng, spa và bar cao cấp trên tầng 41. Tự do nghỉ ngơi và vui chơi khám phá thành phố biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >Nghỉ đêm tại phòng Deluxe hướng biển của khách sạn Best Western Premier Havana Nha Trang</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 03: DU NGOẠN VỊNH ĐẢO NHA TRANG (ĂN: SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Lựa chọn 1: Ở tại khách sạn, tự do tắm biển, tham quan hoặc mua sắm. Khách tự do bữa trưa.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều xe đưa du khách ra ga làm thủ tục về TP.HCM.</span>\r\n            </p>\r\n            <p >\r\n                <span >Lựa chọn 2: Tham quan vịnh đảo Nha Trang (đăng kí khi đặt tour, phụ thu 250.000đ/ khách gồm ăn trưa).</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách dùng bữa sáng tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >09h00: Xe và HDV đón khách tại khách sạn khởi hành du ngoạn đảo.</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Miễu: Tham quan Thủy cung Trí Nguyên, ngắm nhìn sinh vật biển trong lòng thủy cung để khám phá sự đa dạng của hàng trăm loài cá.</span>\r\n            </p>\r\n            <p >\r\n                <span >Bãi Cùi: Bơi, ngắm san hô và cá cảnh biển bằng kính lặn, ống thở, chân vịt (đã bao gồm trong chương trình).</span>\r\n            </p>\r\n            <p >\r\n                <span >Tham gia các trò chơi cảm giác mạnh trên biển: dù lượn, môtô nước, lặn bình hơi khám phá đại dương (chi phí tự túc).</span>\r\n            </p>\r\n            <p >\r\n                <span >Hòn Tằm: Tàu sẽ đưa quý khách đi dọc bờ biển Nha Trang, khám phá nét đẹp lộng lẫy của Hòn Ngọc Việt với góc nhìn đẹp ngắm toàn cảnh thành phố biển Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách dùng cơm trưa trên tàu với những món như: chả cá chiên, thịt kho trứng, cá chiên giòn, rau xào, canh rau, cơm trắng, tráng miệng… Quý khách có thể chọn các loài cá mú, cá chim, tôm hùm, mực tại bè để được phục vụ tại chỗ (chi phí tự túc).</span>\r\n            </p>\r\n            <p >\r\n                <span >Tham gia chương trình văn nghệ giải trí Cây nhà lá vườn” đặc sắc nổi bật với nhiều bài hát từ ban nhạc.</span>\r\n            </p>\r\n            <p >\r\n                <span >Thưởng thức Quán rượu nổi! Một ly rượu, một lát trái cây ngay dưới nước, bạn có thể nâng ly và trò chuyện với bạn bè.</span>\r\n            </p>\r\n            <p >\r\n                <span >Bãi Tranh: Nghỉ ngơi và giải trí tại bãi biển. Nhận ghế lều nghỉ ngơi, thư giãn và tắm biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >Dự tiệc trái cây để thưởng thức hương vị nhiều loại trái cây hiện có ở Nha Trang.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Xe đưa quý khách ra ga làm thủ tục trở về TP.HCM, nghỉ đêm trên tàu cao cấp Golden Train khoang 4 - SNT1 chuyến 19h00. Khách về tới TP.HCM. Kết thúc chương trình tour du lịch Nha Trang 3 ngày.</span>\r\n            </p>', 'Off');
INSERT INTO `tbl_tour` (`id_tour`, `id_destination`, `id_activities`, `Code`, `Name_tour`, `startDate`, `time_tour`, `Price`, `Img`, `Img1`, `Img2`, `Img3`, `Sale`, `Content`, `CtTour`, `Feature`) VALUES
(18, 5, 14, '40-tT', 'Du Lịch Đảo Bình Ba: HCM - Đảo Bình Ba - Đảo Quốc Tôm Hùm - Party Night BBQ ', '2020-07-10', '2 ngày 2 đêm', '1990.000', '1564135188_nha-trang-2.jpg', '1554956741_NhaTrang11.jpg', '1553241755_nha-trang-36.jpg', '1552038296_nha-trang-1.jpg', '', 'Nha Trang biển xanh, cát trắng, nắng vàng - nơi có vô vàn hòn đảo xinh đẹp, có khu vui chơi Vinpearl Land nổi tiếng cả nước với nhiều trò chơi hấp dẫn. Ẩm thực Nha Trang phong phú và hấp dẫn với nhiều món ăn lạ nem nướng, bún cá,.... Đặc biệt không thể không trải nghiệm tắm bùn khoáng Nha Trang nổi tiếng cả nước. Đặt tour  Nha Trang Ninh Thuận 4 ngày ngay hôm nay để cùng chúng tôi trải nghiệm những điều tuyệt vời nhất thiên đường biển xinh đẹp này!', ' <p >\r\n                <span >ĐÊM 01: HÀNH TRÌNH ĐẾN VỚI VỊNH CAM RANH</span>\r\n            </p>\r\n            <p >\r\n                <span >21h00: Xe và Hướng dẫn viên Lữ Hành Việt đón khách tại điểm hẹn. Đoàn khởi hành tham quan Bình Ba. Quý khách nghỉ ngơi trên xe.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 01: VỊNH CAM RANH - ĐẢO BÌNH BA (ĂN 3 BỮA)</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi sáng 06h00: Quý khách dừng chân ăn sáng ở nhà hàng tại Cam Ranh. Sau đó tiếp tục khởi hành đi tham quan Chùa Ốc - một ngôi chùa nổi tiếng ở Cam Ranh được thiết kế từ vỏ Ốc và San Hô.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau đó đến cảng Ba Ngòi khởi hành ra đảo Bình Ba. Trên thời gian tàu di chuyển ra đảo Đoàn sẽ được chiêm ngưỡng vẻ đẹp thiên nhiên hoang sơ với biển xanh dập dờn, những bè nuôi tôm hùm nằm rải rác trên biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >11h00: Đoàn dùng cơm trưa các món ăn đặc sản của Bình Ba tại nhà người dân trên đảo, nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi chiều 14h00: HDV đưa Quý khách ra bến tàu khởi hành tham quan tắm biển lặn ngắm san hô - thưởng thức rượu vang, trái cây tại bãi Nhà Cũ hoặc Hòn Rùa. Tại đây quý khách vừa được tắm biển thưởng thức trái cây vừa nhâm nhi ly rượu vang thả mình vàolàn nước trong xanh của biển đảo - tham gia các trò chơi tại bãi biển (chi phí tự túc). Không những vậy, tại đây Quý khách còn được chụp hình thoải thích, lưu lại những kí niệm trong chuyến đi đầy ý nghĩa.</span>\r\n            </p>\r\n            <p >\r\n                <span >17h30: Đoàn về lại đảo, tự do nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >18h30: Đoàn tập trung và cùng nhau thưởng thức bữa ăn tối thân mật với các món hải sản phong phú, tươi sống,.. do người dân trên đảo chế biến. Đặc biệt Quý khách sẽ được thưởng thức một loại hải sản đặc biệt đã góp phần tạo nên cuộc sống Vương giã cho bà con nơi xứ đảo đó là Tôm Hùm.</span>\r\n            </p>\r\n            <p >\r\n                <span >20h00: Kết thúc bữa tối, quý khách tự do khám phá đảo Bình Ba về đêm, nghỉ đêm tại Bình Ba.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 02: VỊNH CAM RANH - ĐẢO BÌNH BA (ĂN TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi sáng Khoảng 5h00 sáng: HDV đón khách tại bãi xe điện/ xe jeep để khởi hành đi ngắm bình minh trên đảo - trải nghiệm với một trong những nơi đón bình minh đầu tiên trên đất nước Việt Nam và tham quan một vòng quanh đảo, trải nghiệm cảm giác nơi xứ đảo vào buổi sáng, chụp hình check - in tại Hòn Rùa, Bãi Chướng, Đồi cỏ hồng,… Những con đường đảo ngoằn ngoèo và những góc chụp có một không hai.</span>\r\n            </p>\r\n            <p >\r\n                <span >07h00: Quý khách tự do trải nghiệm ẩm thực trên đảo Bình Ba vào buổi sáng với các món đặc sản như: Bún chả cá Bình Ba, Bánh căn, Bánh Khọt,... hoặc thưởng thức café tại những quán café có view nhìn thẳng ra biển mênh mông…</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau đó Đoàn trả phòng và lên tàu khởi hành về lại đất liền và về TP Hồ Chí Minh.</span>\r\n            </p>\r\n            <p >\r\n                <span >12h00: Đoàn dùng cơm trưa tại KDL Cà Ná. Nghỉ ngơi thư giãn, sau đó đoàn tiếp tục khởi hành về Sài Gòn. Đoàn dừng chân tại cơ sở sản xuất nước mắm, mực một nắng, cơ sở sản</span>\r\n            </p>\r\n            <p >\r\n                <span >xuất thanh long tại Bình Thuận.</span>\r\n            </p>\r\n            <p >\r\n                <span >Dự kiến 20h00: Đoàn về tới Sài Gòn, HDV và xe sẽ đưa quý khách về lại điểm đón ban đầu. Kết thúc chuyến tour du lịch Bình Ba cực ký thú vị, HDV thay mặt công ty Lữ Hành Việt gửi tới Quý khách lời cảm ơn và hẹn gặp lại. Kết thúc chương trình tour.</span>\r\n            </p>', 'On'),
(19, 1, 13, '57-tT', 'Du Lịch Tam Đảo - Tây Thiên ', '2020-07-04', '1 ngày', '250.000', '1553738747_cong-thien-vien-tay-thien.jpg', '1553738747_du-lich-tay-thien.jpg', '1553738747_thien-vien-tay-thien-1.jpg', '1553738747_thien-vien-tay-thien-2.jpg', '', 'Hà Nội là thủ đô ngàn năm văn hiến, còn lưu dấu nhiều di tích Hồ Gươm, Cầu Thê Húc, Chùa Quán Sứ, Hồ Tây, 36 phố phường. Hà Nội có bốn mùa, luôn mang đến nhiều hoài niệm khó phai, mỗi mùa một vẻ, xuân lễ hội, hạ tươi thắm, thu quyến rũ và đông ấn tượng. Món ngon có Phở, Chả cá Lã Vọng, bánh tôm Hồ Tây.  ', ' <p >\r\n                <span >06h30: Xe và Hướng dẫn viên&nbsp; đón quý khách tại điểm hẹn khởi hành đi khu du lịch Tây Thiên.</span>\r\n            </p>\r\n            <p >\r\n                <span >08h00: Tới khu du lịch Tây Thiên. Quý khách vào thăm quan và lễ tại Đền Trình, tiếp tục chương trình tham quan Quý khách lễ tại Đền Cậu, trên đường bách bộ tham quan và lễ Đền Cô quý khách thăm Thác Bạc (hoặc Quý khách đến Nhà ga xuất phát từ đền Cậu lên Cáp Treo&nbsp; với chiều dài 2500m trên độ cao khoảng từ 200m tới 500m ngắm cảnh núi rừng Tây Thiên (chi phí Cáp treo tự túc) đến Đền Thượng, Quốc Mẫu Tây Thiên lễ Đền Trung, Đền Thượng, Đền Mẫu .</span>\r\n            </p>\r\n            <p >\r\n                <span >12h30: Quý khách nghỉ ngơi, ăn trưa tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Quý khách bách bộ tham quan Thiền viện Trúc Lâm, tự do thăm quan nghiên cứu đạo phật,…</span>\r\n            </p>\r\n            <p >\r\n                <span >16h00: Quý khách lên xe trở về Hà nội.</span>\r\n            </p>\r\n            <p >\r\n                <span >18h30: Đến Hà Nội, trở lại điểm đón ban đầu, chia tay quý khách, kết thúc chương trình thăm quan Tây Thiên – Thiền Viện Trúc Lâm 1 ngày.</span>\r\n            </p>', 'Off'),
(20, 1, 16, '906-tT', 'Du Lịch Miền Bắc: Đà Nẵng - Hà Nội - Hạ Long - Ninh Bình 4N3Đ, Bay Vietnam Airlines + KS 3*', '2020-07-11', '4 ngày 3 đêm', '5190.000', '1592471119_lang-bac-6.jpg', '1592470727_trang-an-7.jpg', '1592469827_vinh-ha-long-4.jpg', 'gioi-thieu-ve-ha-noi-1.jpg', '', 'Miền Bắc nổi tiếng với nhiều địa điểm, trong đó phải kể tới Thủ đô yêu dấu - Hà Nội, \"kiệt tác thiên nhiên\" vịnh Hạ Long, Ninh Bình... Chuyến tour miền Bắc 4 ngày 3 đêm từ Đà Nẵng sẽ đưa quý khách khám phá trọn vẹn nét đẹp, mang tới cho bạn các trải nghiệm ấn tượng và khó quên.', ' <p >\r\n                <span >NGÀY 1: ĐÀ NẴNG - HÀ NỘI - HẠ LONG (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Dự kiến 06h00: Quý khách tập trung tại Sân bay Quốc nội Đà Nẵng, HDV hỗ trợ làm thủ tục cho quý khách đáp chuyến bay đi Hà Nội (08h00-09h20).</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >09h20: HDV đón quý khách tại sân bay Nội Bài, xe đón đoàn di chuyển về Hạ Long.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Đoàn dừng ăn trưa tại Sao Đỏ - Hải Dương.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau bữa trưa, trên đường đi, xe đưa quý khách tham quan đảo Tuần Châu (chi phí tự túc)xem trình diễn hải cẩu, sư tử biển (show diễn này có thể thay đổi theo chương trình tham quan của Khu du lịch Tuần Châu).</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Hoặc tự do khám phá Sun World Hạ Long Park bằng cáp treo Nữ Hoàng từ bờ biển Bãi Cháy - cáp treo đạt 2 kỷ lục thế giới (cabin có sức chứa lớn nhất thế giới và cáp treo có trụ cáp cao nhất thế giới so với mặt đất) - trải nghiệm vòng quay mặt trời Sun Wheel, tham quan vườn Nhật, khu trưng bày tượng sáp, khu vui chơi trẻ em,…</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa quý khách về Bãi Cháy nhận phòng, nghỉ ngơi tự do tắm biển.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý khách ăn tối tại nhà hàng và tự do dạo chơi Công viên Hoàng Gia dọc biển Bãi Cháy, đi mua sắm hàng hóa tại khu Chợ Đêm Hạ Long sầm uất với các sản phẩm đặc trưng Hạ Long, lên cầu Bãi Cháy - cây cầu dây văng dài nhất Việt Nam ngắm Hạ Long</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Nghỉ đêm khách sạn tại Bãi Cháy.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: VỊNH HẠ LONG - NINH BÌNH (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách ăn sáng tại khách sạn, sau đó xe và HDV đón quý khách khởi hành đi tham quan Vịnh Hạ Long (tham quan bằng tàu 6 tiếng)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Hành trình này sẽ đưa quý khách đi sâu vào vùng lõi của di sản thiên nhiên thế giới Vịnh Hạ Long để tận mắt chứng kiến những tuyệt phẩm của thiên nhiên nơi mà “ NON - NƯỚC - MÂY - TRỜI ” được hòa quyện vào với nhau với những điểm đến tiêu biểu như: Hang Sửng Sốt - Một trong những hang động lớn nhất của Vịnh Hạ Long, Đảo Titop - là hòn đảo nổi tiếng nhất trên vịnh Hạ Long nơi có bãi cát trắng mịn và làn nước trong xanh.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Đoàn ăn trưa trên tàu.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau đó đoàn khởi hành đi Ninh Bình.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Đến Ninh Bình, xe đưa quý khách về khách sạn làm thủ tục nhận phòng, nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Xe đón quý khách đến nhà hàng dùng cơm tối. Quý khách tự do khám phá Ninh Bình về đêm.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: NINH BÌNH - HÀ NỘI (ĂN: SÁNG/TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách dùng điểm tâm sáng tại khách sạn,làm thủ tục trả phòng, bắt đầu hành trình tham quan khám pháBái Đính - Tràng An.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Quý khách cầu lễ phật tại Chùa Bái Đính - ngôi chùa lớn nhất Việt Nam, ngôi chùa này đã sớm nổi tiếng với nhiều kỷ lục:</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chùa có tượng Phật Tổ Như Lai bằng đồng dát vàng lớn nhất Đông Nam Á nặng 100 tấn và ba pho tượng Tam Thế, mỗi pho nặng 50 tấn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chùa có hành lang La Hán dài nhất châu Á, Điện thờ Tam Thế và Pháp Chủ rộng hàng ngàn mét vuông.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Nhìn từ xa, chùa Bái Đính nổi bật trên nền trời xanh giữa những ngọn núi hùng vĩ. Tiếp tục tham quan chùa với hai tháp chuông: Tháp lớn có 3 tầng 24 mái, đặt quả chuông đồng nặng 36 tấn:&nbsp; Nét độc đáo là trên thân chuông có khắc bài kinh Đại Bi Bát Nhã bằng chữ Hán cùng nhiều hoa văn theo chủ đề Thiền học và Phật học.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Đặc biệt, 500 tượng La Hán bằng đá trắng nguyên khối:&nbsp; mỗi vị một vẻ mặt khác nhau được những người thợ chạm khắc rất tinh xảo, sống động.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Những khối gỗ quý, bức hoành phi, câu đối khổng lồ được sơn son thếp vàng cũng đang trong quá trình hoàn thiện, góp phần cho ngôi chùa càng thêm hoành tráng, rực rỡ.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách ăn trưa tại nhà hàng thưởng thức đặc sản dê núi Ninh Bình, Cơm cháy nước sốt, rượu ngọc dương…</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Quý khách đi tham quan Quần thể Danh thắng Tràng An - một Hạ Long trên cạn của Ninh Bình, xuống thuyền tham quan quần thể hang động: hang Sáng, hang Tối, hang Ba Giọt, hang Nấu rượu đầy huyền bí và hoang sơ, ngắm cảnh núi non, sông nước trong xanh.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau đó đoàn khởi hành về Hà Nội.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Quý khách về đến Hà Nội, làm thủ tục nhận phòng khách sạn, nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau đó, xe và HDV đưa quý khách đến nhà hàng dùng cơm tối. Nghỉ đêm tại Hà Nội. (Buổi tối, quý khách tự do vui chơi Phố đi bộ Tạ Hiện, bar 1900, Hồ Gươm…).</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 4: HÀ NỘI - CITY TOUR - ĐÀ NẴNG (ĂN SÁNG/TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách ăn sáng tại khách sạn, làm thủ tục trả phòng.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa đoàn tham quan viếng Lăng Bác - nơi đặt thi hài của chủ tịch Hồ Chí Minh, vị anh hùng dân tộc Quảng Trường Ba Đình – quảng trường lớn nhất Việt Nam, nằm ngay phía trước lăng chủ tịch. Nhà Sàn giản dị nằm giữa thủ đô Hà Nội là nơi Bác Hồ đã sống và làm việc lâu nhất trong cuộc đời hoạt động cách mạng của mình Ao Cá Bác Hồ với diện tích 3.320 m², sâu 3 m, có rất nhiều loài cá được thả tại đây.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chùa Một Cột (Chùa Mật) - ngôi chùa có kiến trúc độc đáo, chỉ có 1 gian dựng trên 1 cột đá nằm giữa lòng hồ Linh Chiểu.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Văn Miếu Quốc Tử Giám - ngôi trường đại học đầu tiên của Việt Nam. Đặc biệt, đây còn là nơi các sĩ tử ngày nay đến cầu may trước mỗi kỳ thi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Ăn trưa tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe đưa Quý khách tham quan:</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Đền Ngọc Sơn - viên ngọc của Hồ Hoàn Kiếm, đặt chân lên cầu Thê Húc được ví như chiếc lược thắm cài lên mái tóc xanh thiếu nữ của hồ Hoàn Kiếm, ngắm nhìn Tháp Rùa phía xa và chiêm ngưỡng cụ rùa nghìn năm tuổi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Dự kiến 14h30: Xe đưa Qúy khách ra sân bay, làm thủ tục bay về Đà Nẵng (17h00-18h25). Chia tay &amp; hẹn gặp lại du khách trong những hành trình tiếp theo.</span>\r\n            </p>', 'Off'),
(21, 1, 14, '776-tT', 'BẢNG GIÁ PHÒNG HỆ THỐNG FLC TOÀN QUỐC 2020', '2020-07-12', '3 ngày 2 đêm', '1200.000 ', '1578644897_flc-ss-dep.jpg', '1578644897_flc-sam-son-resort.jpg', '1578644897_flc-resort-sam-son.jpg', '1578644897_flc-ss-dep.jpg', '', 'Hà Nội là thủ đô ngàn năm văn hiến, còn lưu dấu nhiều di tích Hồ Gươm, Cầu Thê Húc, Chùa Quán Sứ, Hồ Tây, 36 phố phường. Hà Nội có bốn mùa, luôn mang đến nhiều hoài niệm khó phai, mỗi mùa một vẻ, xuân lễ hội, hạ tươi thắm, thu quyến rũ và đông ấn tượng. Món ngon có Phở, Chả cá Lã Vọng, bánh tôm Hồ Tây.  ', ' <p >\r\n                <span >Các điểm đến của FLC</span>\r\n            </p>\r\n            <p >\r\n                <span >FLC HẠ LONG - FLC VĨNH PHÚC - FLC SẦM SƠN- FLC QUY NHƠN</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trải nghiệm “thiên đường nghỉ dưỡng sống động” giữa đời thực với các dịch vụ đẳng cấp đạt&nbsp; tiêu chuẩn 5 * như sân golf, resort, biệt thự nghỉ nghỉ dưỡng , khu vui chơi giải trí đẳng cấp và rất nhiều những tiện ích khác.</span>\r\n            </p>', 'On'),
(22, 1, 13, '405-tT', 'Du Lịch Nam Định - Đền Trần - Phủ Giầy - Cổ Lễ 1 Ngày - Du Xuân Đầu Năm', '2020-07-11', '1 Ngày', '550.000', 'tour-le-hoi-den-tran-phu-giay-1-ngay.jpg', 'tour-le-hoi-den-tran-phu-giay-1-ngay.jpg', 'tour-le-hoi-den-tran-phu-giay-1-ngay.jpg', 'tour-le-hoi-den-tran-phu-giay-1-ngay.jpg', '', 'Hà Nội là thủ đô ngàn năm văn hiến, còn lưu dấu nhiều di tích Hồ Gươm, Cầu Thê Húc, Chùa Quán Sứ, Hồ Tây, 36 phố phường. Hà Nội có bốn mùa, luôn mang đến nhiều hoài niệm khó phai, mỗi mùa một vẻ, xuân lễ hội, hạ tươi thắm, thu quyến rũ và đông ấn tượng. Món ngon có Phở, Chả cá Lã Vọng, bánh tôm Hồ Tây.  ', '<p >\r\n                <span >06h00: Xe và hướng dẫn viên của Công ty đón Quý khách tại điểm hẹn khởi hành đi Nam Định.</span>\r\n            </p>\r\n            <p >\r\n                <span >Điểm đầu tiên quý khách thăm quan và lễ là Đền Trần, nghe giới thiệu lịch sử xây dựng đất nước, và 3 lần đánh thắng quân Nguyên – Mông của các vua quan thời Trần. Sau đó Đoàn lên xe đi thăm quan chùa Cổ Lễ hay còn gọi là Quang Thần tự là một ngôi chùa nổi tiếng nằm ở thị trấn Cổ Lễ, huyện Trực Ninh, tỉnh Nam Định.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trong chùa có quả chuông Đại Hồng Chung lớn nhất Việt Nam được Hòa thượng Thích Thế Long cho đúc vào năm 1936.</span>\r\n            </p>\r\n            <p >\r\n                <span >11h00: Quý khách lên xe đi ăn trưa, nghỉ ngơi tại nhà hàng.</span>\r\n            </p>\r\n            <p >\r\n                <span >13h00: Tiếp tục đi thăm quan và Lễ Phủ Giầy (nơi thờ bà chúa Liễu Hạnh – một nơi nổi tiếng và linh thiêng) tại đây quý khách sẽ tìm hiểu về tín ngưỡng thờ Mẫu của dân gian Việt Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >16h00: Quý khách lên xe về Hà Nội.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >18h30: Đoàn về tới Hà Nội, chia tay quý khách, kết thúc chương trình du lịch Hà Nội - Phủ Giầy – Đền Trần – Cổ Lễ 1 ngày.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>', 'Off'),
(23, 1, 13, '587-tT', 'u Lịch Hà Nam - Chùa Tam Chúc 1 Ngày', '2020-07-03', '1 Ngày', '1000.000', '1554357850_du-lich-Vuon-Quoc-Gia-Cuc-Phuong-3.jpg', '1554357850_du-lich-Vuon-Quoc-Gia-Cuc-Phuong-2.jpg', '1554357850_du-lich-Vuon-Quoc-Gia-Cuc-Phuong-4.jpg', '1554357850_du-lich-Vuon-Quoc-Gia-Cuc-Phuong-3.jpg', '', 'Hà Nội là thủ đô ngàn năm văn hiến, còn lưu dấu nhiều di tích Hồ Gươm, Cầu Thê Húc, Chùa Quán Sứ, Hồ Tây, 36 phố phường. Hà Nội có bốn mùa, luôn mang đến nhiều hoài niệm khó phai, mỗi mùa một vẻ, xuân lễ hội, hạ tươi thắm, thu quyến rũ và đông ấn tượng. Món ngon có Phở, Chả cá Lã Vọng, bánh tôm Hồ Tây.  ', ' <p >\r\n                <span >Đồng hành cùng Lữ Hành Việt trong chuyến đi chiếm bái Phật tổ tại Chùa Tam Chúc quý khách sẽ có cơ hội trải nghiệm những hoạt động thú vị:</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >✔️Làm lễ, thắp hương và tham quan khu văn hóa tâm linh Tam Chúc được công nhận là khu DL trọng điểm quốc gia. Bao gồm Khu văn hóa tâm linh; Khu trung tâm đón tiếp nghỉ dưỡng; sân golf 36 lỗ; Khu cây xanh và dịch vụ ven hồ; Khu bảo tồn di tích và cảnh quan thiên nhiên.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >✔️Ghé tham quan và thắp hương tại đền Trần Thương, ngôi chùa thiêng liêng hơn 7 thế kỷ, nơi thờ vị anh hùng dân tộc Quốc công Tiết chế Hưng Đạo Đại Vương Trần Quốc Tuấn cùng gia quyến và các Bộ tướng có công trong cuộc kháng chiến chống quân Nguyên Mông thế kỷ XIII.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chương trình: 01 NGÀY</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >06h30 : Xe ôtô và hướng dẫn viên của Công ty OPENTOUR đón quý khách tại điểm hẹn trong thành phố khởi hành đi chiếm bái Phật tổ tại Chùa Tam Chúc</span>\r\n            </p>\r\n            <p >\r\n                <span >9h00: Xe đưa đoàn đến khu văn hóa tâm linh Tam Chúc được công nhận là khu DL trọng điểm quốc gia. Tam Chúc là vùng núi đá vôi ngập nước rất độc đáo, với phong cảnh nước non hùng vỹ Bao gồm Khu lòng hồ; Khu văn hóa tâm linh; Khu trung tâm đón tiếp nghỉ dưỡng; sân golf 36 lỗ; Khu cây xanh và dịch vụ ven hồ; Khu bảo tồn di tích và cảnh quan thiên nhiên. Đoàn làm lễ và thắp hương tại chùa Tam Chúc.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Theo quan niệm của nhà Phật, ý nghĩa của lễ Cầu an là nhằm mục đích sám hối tội lỗi, dứt trừ nghiệp chướng, tránh mọi bệnh tật, tai họa và nghiệp báo, để được thân tâm an lạc, phước huệ trang nghiêm, đạo tâm kiên cố, Bồ đề tâm tăng trưởng. Với ý nghĩa ấy, trong những ngày đầu năm mới, mỗi người con Phật sẽ lắng lòng nhìn nhận lỗi lầm từ những Tham - Sân - Si của Tâm và nguyện sẽ sám hối, chừa bỏ, cầu cho một năm mới hạnh phúc, an vui.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đặc biệt nơi đây sẽ diễn ra Đại lễ Phật Đản Liên Hiệp Quốc lớn nhất thế giới Vesak 2019, lần thứ 2 được tổ chức tại Việt Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >11h00: Đoàn ăn trưa tại nhà hàng trên đường đi, thưởng thức ẩm thực Hà Nam.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >14h00: Điểm dừng chân tiếp theo là đền Trần Thương, ngôi chùa thiêng liêng hơn 7 thế kỷ, thuộc thôn Trần Thương, xã Nhân Đạo, huyện Lý Nhân, tỉnh Hà Nam. Đền thờ vị anh hùng dân tộc Quốc công Tiết chế Hưng Đạo Đại Vương Trần Quốc Tuấn cùng gia quyến và các Bộ tướng có công trong cuộc kháng chiến chống quân Nguyên Mông thế kỷ XIII.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Dân gian có câu: “Sinh Kiếp Bạc, thác Trần Thương, quê hương Bảo Lộc. Từ Kiếp Bạc, Chí Linh, Hải Dương đến đền Trần Thương, Lý Nhân, Hà Nam về đến Bảo Lộc -Nam Định khách hành hương theo lộ trình chiêm bái, tôn vinh người anh hùng dân tộc, đã được truy tôn là bậc “Thánh, ban những điều tốt lành, mong làm việc thiện.</span>\r\n            </p>\r\n            <p >\r\n                <span >15h30: Đoàn lên xe tạm biệt mảnh đất thiêng Nhân Đạo, trở về Hà Nội.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >17h00: Về tới Hà Nội, kết thúc chuyến đi tốt đẹp, HDV chia tay đoàn và hẹn gặp lại trong các chương trình tiếp theo.</span>\r\n            </p>', 'On'),
(25, 2, 16, '540-tT', 'Du Lịch Đà Lạt: Đà Nẵng - Đà Lạt - Thành Phố Ngàn Hoa 4 Ngày + Vé Máy Bay & KS 3*', '2020-07-12', '4 ngày 3 đêm', '4000.000 ', '1592574466_da-lat-cadasa.jpg', '1592574466_da-lat-lang-cu-lan.jpg', '1592574517_da-lat-que-garden.jpg', '1592574517_du-lich-da-lat.jpg', '4.550.000', 'Nằm ở phía Đông Bắc hồ Xuân Hương, vườn hoa được khởi công xây dựng ngày 21-6-1973 và phát triển từ năm 1985, rộng 11ha. Đây là nơi hội tụ nhiều loài hoa đẹp của phương Đông và phương Tây. Ngoài các giống hoa truyền thống mà du khách đã biết như cẩm tú cầu, hồng, hồng ri, xác pháo, mimosa,…; tại vườn hoa còn có hàng chục giống hoa mới được du nhập vào Đà Lạt.', '<p >\r\n                <span >NGÀY&nbsp; 01: ĐÀ NẴNG - ĐÀ LẠT (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Dự kiến 07h00: Hướng dẫn viên đón Quý khách tại sân bay Đà Nẵng, làm thủ tục hỗ trợ Quý khách đáp chuyến bay đi Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >10h35: Đến sân bay Liên Khương, xe và hướng dẫn viên đón khách, khởi hành về trung tâm Đà Lạt, dùng bữa trưa. Nhận phòng khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều:</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe và HDV đưa quý khách tham quan tham quan :</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Quảng trường Lâm Viên - một trong những công trình xây dựng hoành tráng nhất của thành phố ngàn hoa Đà Lạt. Được xem như trái tim không thể thiếu của thành phố, quảng trường Lâm Viên trở thành điểm dừng chân của hàng ngàn du khách mỗi khi đặt chân đến Đà Lạt, và là nơi hứa hẹn cho những shoot ảnh cực chảnh.</span>\r\n            </p>\r\n            <p >\r\n                <span >Thăm nhà thờ Domain De Marie với lối kiến trúc Pháp cổ kính, nơi lưu giữ phần mộ phu nhân toàn quyền Đông Dương một thời. Sau đó về khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >Núi Langbiang Tiếp tục hành trình tới chân núi Langbiang (cao 2.169m), quý khách thuê xe Jeep lên núi (tự túc). Từ đỉnh núi quý khách sẽ cảm nhận được không khí mát mẻ dễ chịu của núi rừng, ngắm cảnh chụp hình bao quát thành phố Đà Lạt, hồ suối vàng, suối bạc…</span>\r\n            </p>\r\n            <p >\r\n                <span >Xã Lát Ghé vào làng đồng bào K’ho thăm quan đời sống, văn hóa, phong tục tập quán đồng bào. Xem làm thổ cẩm, nghe già làng kể chuyện.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Xe và HDV đưa quý khách dùng cơm tối tại nhà hàng. Quý khách tham gia chương trình đặc sắc của Đà Lạt như giao lưu Cồng chiêng Tây Nguyên (chi phí tự túc).</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 02 :&nbsp; ĐÀ LẠT SỨ SỞ NGÀN THÔNG (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng : Quý khách dùng điểm tâm buffet sáng tại khách sạn. Sau đó xe đưa quý khách đi tham quan :</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Phố bích hoạ Dốc Nhà Làng - Dốc Nhà Làng không còn quá xa lạ với bao thế hệ người dân Đà Lạt cũng như du khách đến đây, nhưng sau hơn nửa thập kỉ, hơn 5 con hẻm nhỏ vô tri vô giác này bỗng được thổi hồn và làm mới mình bởi các nghệ sĩ trẻ tải năng đến từ Phố Bên Đồi. Bạn sẽ bắt gặp những hình ảnh quen thuộc vốn gắn liền với thành phố ngàn hoa bao thập kỉ qua như hình ảnh bác sĩ Yersin – người mà Đà Lạt được vinh dự lấy tên để đặt cho một con đường khá nổi tiếng, hay là trường Trung học Bùi Thị Xuân bên tà áo dài trắng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đồi Robin (Đồi vọng cảnh) &amp; Ga cáp treo Đà Lạt, quý khách ngắm toàn cảnh thành phố Đà Lạt xinh đẹp, nếu có nhu cầu quý khách có thể trải nghiệm cáp treo từ đây sang Thiền Viện Trúc Lâm (Tự phí: 80,000đ/1 chiều, 100,000đ/khứ hồi)</span>\r\n            </p>\r\n            <p >\r\n                <span >Green Hill - Địa điểm vàng để có những bức hình lung linh tuyệt đẹp ở Đà Lạt với các cảnh quan: Cổng trời Bali, Cổng thần đạo Nhật Bản, nấc thang lên thiên đường… (Chi phí nước uống vào chụp ảnh quý khách tự thanh toán)</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách dùng Buffet rau Leguda - nét văn hoá ẩm thực của Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tiếp tục hành trình tham quan:</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Thiền viện Trúc Lâm - Hồ Tuyền Lâm ngắm phong cảnh non nước hữu tình ở nơi cửa phật yên bình và thanh thản. Khi đến đây du khách sẽ cảm thấy thoải mái, rũ bỏ những bộn bề và lo toan của cuộc sống tấp nập hàng ngày.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đường hầm điêu khắc bằng Đất Sét dài hơn 1km, đường hầm điêu khắc tại hiện lại lịch sử hình thành Đà Lạt từ thuở khai sơ đến hiện đại với một số công trình đặc trưng như : Trường cao đẳng sư phạm, Ga xe Lửa Đà Lạt, các nhà thờ, thiền viện, biệt thự cổ, đỉnh núi Langbiang,… Bên cạnh đó là mô hình các vật dụng, phương tiện gắn với người dân phố núi như: xe hơi cổ, vespa cổ, đầu xe lửa hơi nước, điện thoại cổ,…</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Xe và HDV đưa quý khách về khách sạn, quý khách tự do ăn tối. Sau đó quý khách tự do tham quan chợ đêm Đà Lạt hoặc thưởng thức ẩm thực đêm của Đà Lạt như sữa đậu nóng, pizza Đà Lạt, Ngô nướng, Khoai Lang nướng,… Nghỉ đêm tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 03: ĐÀ LẠT THÔNG REO - ĐÀ NẴNG (Ăn SÁNG, TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng : Quý khách dùng điểm tâm buffet sáng tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đồi Chè Cầu Đất với màu xanh bạt ngàn của lá chè không khí trong lành của Đà Lạt là điểm đến lý tưởng cho những ai muốn tìm về chốn đồng quê khi đến với thành phố mộng mơ Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Vườn hoa Cẩm Tú Cầu - nơi hàng ngàn bông hoa cẩm tú rực rỡ trong nắng mai với trào lưu check-in không thể thiếu khi đến Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >Quý khách dùng bữa trưa.</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe và HDV đưa quý khách tham quan :</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Ga xe lửa cổ Nhà ga cổ đẹp nhất Đông Dương. Nhà ga cao nhất Việt Nam. Xây dựng từ những năm đầu thế kỷ trước, mang nét hài hòa giữa kiến trúc phương Tây và kiểu nhà rông Tây nguyên truyền thống. Quý khách sẽ tham quan chiếc đầu máy chạy bằng hơi nước được sản xuất từ năm 1936 với những thanh ray răng cưa ngày xưa khi tàu vượt qua đèo Ngoạn Mục.</span>\r\n            </p>\r\n            <p >\r\n                <span >Viếng chùa Linh Phước hay còn gọi chùa Ve Chai với lối kiến trúc khảm sành đặc trưng. Tại chùa Linh Phước có đến hàng trăm kỉ lục, hàng chục công trình ấn tượng như tượng sáp, tượng phật bằng hoa bất tử, ...khám phá bàn xoay kì bí...</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn dùng bữa tối với thực đơn lẩu &amp; nướng BBQ và tự do tận hưởng không khí đêm trong lành tại Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 04: ĐÀ LẠT THÔNG REO - ĐÀ NẴNG (Ăn SÁNG)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng : Quý khách dùng điểm tâm buffet sáng tại khách sạn. Tự do mua sắm các loại đặc sản phố núi tại chợ Đà Lạt hoặc khám phá thành phố với xe máy tự túc.</span>\r\n            </p>\r\n            <p >\r\n                <span >Sau đó xe và HDV đưa quý khách ra sân bay Liên Khương, làm thủ tục đáp chuyến bay về Đà Nẵng. Kết thúc chương trình tham quan. Chia tay quý khách và hẹn gặp lại.</span>\r\n            </p>', 'Off'),
(26, 2, 16, '799-tT', 'Du Lịch Đà Lạt: Đà Nẵng - Đà Lạt - Thành Phố Ngàn Hoa 3 Ngày + Vé Máy Bay & KS 3*', '2020-07-15', '3 ngày 2 đêm', '2000.000 ', '1591241582_nha-ga-xe-lua-da-lat-1.jpg', '1591241582_ho-tuyen-lam-1.jpg', '1591241368_langbiang-da-lat-2.jpg', 'dalat.jpg', '2.550.000', 'Nằm ở phía Đông Bắc hồ Xuân Hương, vườn hoa được khởi công xây dựng ngày 21-6-1973 và phát triển từ năm 1985, rộng 11ha. Đây là nơi hội tụ nhiều loài hoa đẹp của phương Đông và phương Tây. Ngoài các giống hoa truyền thống mà du khách đã biết như cẩm tú cầu, hồng, hồng ri, xác pháo, mimosa,…; tại vườn hoa còn có hàng chục giống hoa mới được du nhập vào Đà Lạt.', '<p >\r\n                <span >NGÀY 01: ĐÀ NẴNG - ĐÀ LẠT (ĂN TRƯA, TỐI)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Dự kiến 09h00: Hướng dẫn viên đón Quý khách tại sân bay Đà Nẵng, làm thủ tục hỗ trợ Quý khách đáp chuyến bay đi Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >12:10: Đến sân bay Liên Khương, xe và hướng dẫn viên đón khách, khởi hành về trung tâm Đà Lạt, dùng bữa trưa. Nhận phòng khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều:</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe và HDV đưa quý khách tham quan tham quan Quảng trường Lâm Viên - một trong những công trình xây dựng hoành tráng nhất của thành phố ngàn hoa Đà Lạt. Được xem như trái tim không thể thiếu của thành phố, quảng trường Lâm Viên trở thành điểm dừng chân của hàng ngàn du khách mỗi khi đặt chân đến Đà Lạt, và là nơi hứa hẹn cho những shoot ảnh cực chảnh.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Thăm nhà thờ Domain De Marie với lối kiến trúc Pháp cổ kính, nơi lưu giữ phần mộ phu nhân toàn quyền Đông Dương một thời.&nbsp; Sau đó về khách sạn nghỉ ngơi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Núi Langbiang Tiếp tục hành trình tới chân núi Langbiang (cao 2.169m), quý khách thuê xe Jeep lên núi (tự túc). Từ đỉnh núi quý khách sẽ cảm nhận được không khí mát mẻ dễ chịu của núi rừng, ngắm cảnh chụp hình bao quát thành phố Đà Lạt, hồ suối vàng, suối bạc…</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Xã Lát Ghé vào làng đồng bào K’ho thăm quan đời sống, văn hóa, phong tục tập quán đồng bào. Xem làm thổ cẩm, nghe già làng kể chuyện.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Xe và HDV đưa quý khách dùng cơm tối tại nhà hàng. Quý khách tham gia chương trình đặc sắc của Đà Lạt như giao lưu Cồng chiêng Tây Nguyên (chi phí tự túc).</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY&nbsp; 02: ĐÀ LẠT SỨ SỞ NGÀN THÔNG (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách dùng điểm tâm buffet sáng tại khách sạn. Sau đó xe đưa quý khách đi tham quan :</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Phố bích hoạ Dốc Nhà Làng - Dốc Nhà Làng không còn quá xa lạ với bao thế hệ người dân Đà Lạt cũng như du khách đến đây, nhưng sau hơn nửa thập kỉ, hơn 5 con hẻm nhỏ vô tri vô giác này bỗng được thổi hồn và làm mới mình bởi các nghệ sĩ trẻ tải năng đến từ Phố Bên Đồi. Bạn sẽ bắt gặp những hình ảnh quen thuộc vốn gắn liền với thành phố ngàn hoa bao thập kỉ qua như hình ảnh bác sĩ Yersin - người mà Đà Lạt được vinh dự lấy tên để đặt cho một con đường khá nổi tiếng, hay là trường Trung học Bùi Thị Xuân bên tà áo dài trắng.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Đồi Robin (Đồi vọng cảnh) &amp; Ga cáp treo Đà Lạt, quý khách ngắm toàn cảnh thành phố Đà Lạt xinh đẹp, nếu có nhu cầu quý khách có thể trải nghiệm cáp treo từ đây sang Thiền Viện Trúc Lâm (Tự phí: 80,000đ/1 chiều, 100,000đ/khứ hồi)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Green Hill - Địa điểm vàng để có những bức hình lung linh tuyệt đẹp ở Đà Lạt với các cảnh quan: Cổng trời Bali, Cổng thần đạo Nhật Bản, nấc thang lên thiên đường… (Chi phí nước uống vào chụp ảnh quý khách tự thanh toán)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách dùng Buffet rau Leguda - nét văn hoá ẩm thực của Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Tiếp tục hành trình tham quan:</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Thiền viện Trúc Lâm - Hồ Tuyền Lâm ngắm phong cảnh non nước hữu tình ở nơi cửa phật yên bình và thanh thản. Khi đến đây du khách sẽ cảm thấy thoải mái, rũ bỏ những bộn bề và lo toan của cuộc sống tấp nập hàng ngày.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Đường hầm điêu khắc bằng Đất Sét dài hơn 1km, đường hầm điêu khắc tại hiện lại lịch sử hình thành Đà Lạt từ thuở khai sơ đến hiện đại với một số công trình đặc trưng như : Trường cao đẳng sư phạm, Ga xe Lửa Đà Lạt, các nhà thờ, thiền viện, biệt thự cổ, đỉnh núi Langbiang,… Bên cạnh đó là mô hình các vật dụng, phương tiện gắn với người dân phố núi như: xe hơi cổ, vespa cổ, đầu xe lửa hơi nước, điện thoại cổ,…</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Tối: Xe và HDV đưa quý khách về khách sạn, quý khách tự do ăn tối. Sau đó quý khách tự do tham quan chợ đêm Đà Lạt hoặc thưởng thức ẩm thực đêm của Đà Lạt như sữa đậu nóng, pizza Đà Lạt, Ngô nướng, Khoai Lang nướng,… Nghỉ đêm tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 03: ĐÀ LẠT THÔNG REO - ĐÀ NẴNG (ĂN SÁNG)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách dùng điểm tâm buffet sáng tại khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Đồi Chè Cầu Đất với màu xanh bạt ngàn của lá chè không khí trong lành của Đà Lạt là điểm đến lý tưởng cho những ai muốn tìm về chốn đồng quê khi đến với thành phố mộng mơ Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Vườn hoa Cẩm Tú Cầu - nơi hàng ngàn bông hoa cẩm tú rực rỡ trong nắng mai với trào lưu check-in không thể thiếu khi đến Đà Lạt. Cẩm Tú Cầu - loài hoa không chỉ xinh đẹp về màu sắc và cánh hoa. Mà nó hoa còn mang đến nhiều ý nghĩa tượng trưng vô cùng đặc biệt. Khiến cho những ai khi thấy loài hoa này đều phải lưu luyến, băn khoăn không nỡ rời đi.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe và HDV đưa quý khách tham quan Ga xe lửa cổ Nhà ga cổ đẹp nhất Đông Dương. Nhà ga cao nhất Việt Nam. Xây dựng từ những năm đầu thế kỷ trước, mang nét hài hòa giữa kiến trúc phương Tây và kiểu nhà rông Tây nguyên truyền thống. Quý khách sẽ tham quan chiếc đầu máy chạy bằng hơi nước được sản xuất từ năm 1936 với những thanh ray răng cưa ngày xưa khi tàu vượt qua đèo Ngoạn Mục.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe và hdv đưa quý khách mua sắm đặc sản đà lạt về làm quà tại Chợ Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Trưa: Quý khách tự túc dùng cơm trưa tại nhà hàng. Sau đó quý khách trả phòng khách sạn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >11h00: Xe và HDV đưa quý khách ra sân bay Liên Khương, làm thủ tục đáp chuyến bay về Đà Nẵng. Kết thúc chương trình tham quan. Chia tay quý khách và hẹn gặp lại.</span>\r\n            </p>', 'Off'),
(28, 2, 16, '181-tT', 'Tour Đà Lạt: HCM - Đà Lạt - Xứ Sở Sương Mù 3 Ngày 2 Đêm', '2020-07-11', '3 Ngày 2 Đêm', '2500.000 ', '1554723399_tour-du-lich-da-lat-3.jpg', '1554723399_tour-du-lich-da-lat-2.jpg', '1554723399_tour-du-lich-da-lat-4.jpg', '1592574517_da-lat-que-garden.jpg', '', 'Nằm ở phía Đông Bắc hồ Xuân Hương, vườn hoa được khởi công xây dựng ngày 21-6-1973 và phát triển từ năm 1985, rộng 11ha. Đây là nơi hội tụ nhiều loài hoa đẹp của phương Đông và phương Tây. Ngoài các giống hoa truyền thống mà du khách đã biết như cẩm tú cầu, hồng, hồng ri, xác pháo, mimosa,…; tại vườn hoa còn có hàng chục giống hoa mới được du nhập vào Đà Lạt.', '<p >\r\n                <span >NGÀY 1: HCM - ĐÀ LẠT (ĂN SÁNG, TRƯA, CHIỀU)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi sáng, cắt đầu chương trình tour Đà Lạt 3 ngày từ HCM, đón khách tại điểm hẹn, xe đưa du khách đi Đà Lạt. Quý khách ghé tham quan thác Damb’ri một trong những ngọn thác nổi tiếng đẹp và hùng vĩ ở Bảo Lộc.</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi chiều, đến Đà Lạt, về khách sạn Quý khách nhận phòng.</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi tối, Quý khách đi dạo Đà Lạt về đêm, thưởng thức các món ngon ở chợ đêm Âm Phủ, hương vị cà phê phố núi ở quán Thủy Tạ, ngắm cảnh Hồ Xuân Hương, nghỉ đêm tại Đà Lạt</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2 : ĐÀ LẠT - CÁC THẮNG CẢNH (ĂN SÁNG, TRƯA, CHIỀU)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi sáng, Quý khách đi thưởng ngoạn quần thể phong cảnh đẹp nhất Cao nguyên Lâm Viên như: thác Ankroet, hồ Suối Vàng, hồ Dankia, Thung Lũng Vàng... một không gian huyền thoại, nơi lưu dấu bước chân người mở cõi; trên đường đi Quý khách ngắm cảnh ngoại ô Đà Lạt với những nhà vườn trồng đủ loại hoa màu. Tiếp tục xe đưa Quý khách viếng nhà thờ Domain-de Marie, một công trình kiến trúc đặc sắc với không gian hài hòa ấm cúng cùng nhiều loài kỳ hoa dị thảo đặc trưng của vùng đất Tây nguyên.</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi chiều, Quý khách tham quan Biệt Điện Bảo Đại, Vườn hoa Bích Câu. Ghé chợ Đà Lạt mua đặc sản, hoặc tự do đi xe ngựa cổ 4 bánh dạo quanh Hồ Xuân Hương.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi tối, Quý khách tự do hoặc tham dự đêm lửa trại, uống rượu cần, văn nghệ cồng chiêng, giao lưu văn hóa với người Tây Nguyên (chi phí tự túc), nghỉ đêm tại Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: ĐÀ LẠT - HCM (ĂN SÁNG, TRƯA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi sáng, Quý khách trả phòng, viếng Thiền Viện Trúc Lâm, đi cáp treo qua đồi Rôbin, ngắm cảnh rừng thông, hồ Tuyền Lâm, núi Phượng Hoàng từ trên cao. Sau đó, xe đưa đoàn về Thành Phố Bảo Lộc, Quý khách thưởng thức đặc sản trà, cà phê nổi tiếng nơi đây.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đoàn khởi hành về TP.Hồ Chí Minh theo cung đường Ngã ba Cát Lái - hầm Thủ Thiêm, hầm vượt sông dài nhất Đông Nam Á.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Buổi chiều, đoàn về đến Sài Gòn, chia tay tạm biệt, kết thúc tour du lịch Đà Lạt 3 ngày từ HCM, hẹn tái ngộ cùng Quý khách.</span>\r\n            </p>', 'Off');
INSERT INTO `tbl_tour` (`id_tour`, `id_destination`, `id_activities`, `Code`, `Name_tour`, `startDate`, `time_tour`, `Price`, `Img`, `Img1`, `Img2`, `Img3`, `Sale`, `Content`, `CtTour`, `Feature`) VALUES
(30, 2, 17, '155-tT', 'Du Lịch Đà Lạt: Sài Gòn - Đà Lạt - Thành Phố Ngàn Hoa', '2020-10-08', '4 ngày 3 đêm', '2190.000 ', '1554538134_du-lich-da-lat-3.jpg', '1554538134_du-lich-da-lat-2.jpg', '1554538134_du-lich-da-lat-4.jpg', 'dalat.jpg', '', 'Nằm ở phía Đông Bắc hồ Xuân Hương, vườn hoa được khởi công xây dựng ngày 21-6-1973 và phát triển từ năm 1985, rộng 11ha. Đây là nơi hội tụ nhiều loài hoa đẹp của phương Đông và phương Tây. Ngoài các giống hoa truyền thống mà du khách đã biết như cẩm tú cầu, hồng, hồng ri, xác pháo, mimosa,…; tại vườn hoa còn có hàng chục giống hoa mới được du nhập vào Đà Lạt.', '<p >\r\n                <span >NGÀY 1: TP HỒ CHÍ MINH - BẢO LỘC - ĐÀ LẠT (ĂN 3 BỮA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Khoảng 5h30 xe và HDV đón khách tại điểm hẹn tập trung khởi hành đi Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >Địa điểm 1: Nhà văn hóa thanh niên số 4 Phạm Ngọc Thạch.</span>\r\n            </p>\r\n            <p >\r\n                <span >Địa điểm 2: Toàn nhà Nam Việt, Số 09 Phan Kế Bính, Phường Đa Kao, Quận 1.</span>\r\n            </p>\r\n            <p >\r\n                <span >Địa điểm 3: Cây xăng Comeco số 3 gần Ngã Tư Hàng Xanh.</span>\r\n            </p>\r\n            <p >\r\n                <span >Dùng điểm tâm tại ngã ba Dầu Giây. Tiếp tục lộ trình du khách sẽ ngoạn cảnh Rừng Cây Giá Tỵ, Làng Nuôi Cá Bè. Đến Bảo Lộc dùng cơm trưa.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Tiếp tục lộ trình đi Đà Lạt tham quan Thác Datanla_Nằm cách trung tâm thành phố Đà Lạt chừng 10km, trên con đường đèo Prenn, thác Datanla_địa điểm du lịch nổi tiếng hiện lên thật hùng vĩ nhưng vẫn mang nét đẹp thơ mộng trữ tình. Ngắm thác Datanla trong tiết trời mát lành, giữa rừng xanh bất tận sẽ đem đến cho bạn cảm giác thư thái tuyệt vời.</span>\r\n            </p>\r\n            <p >\r\n                <span >Đến Đà Lạt nhận phòng, dùng cơm chiều.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 2: ĐỒI CHÈ CẦU ĐẤT - RỪNG HOA KHÔ (ĂN 2 BỮA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Dùng điểm tâm Buffet.</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa đoàn đi tham quan Đồi chè Cầu Đất, nằm cách trung tâm Đà Lạt 30km, đồi chè Cầu Đất là điểm đến quen thuộc, ưa thích của nhiều bạn trẻ khi đến thành phố sương mù Đà Lạt. Ngoài việc chiêm ngưỡng khung cảnh vừa yên bình, vừa hùng vỹ của đồi chè và vườn ươm có diện tích lên đến 220ha. Nơi đây còn là điểm tham quan chụp ảnh và còn là địa điểm của những cặp đôi đến đây để chụp ảnh cưới lưu niệm.</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa đoàn tham quan chùa Linh Phước_tọa lạc tại số 120 Tự Phước, thuộc địa bàn Trại Mát, cách trung tâm thành phố Đà Lạt 8 km, trên quốc lộ 20. Chùa còn được gọi là Chùa Ve Chai vì trong sân chùa có con rồng dài 49 m được làm bằng 12.000 vỏ chai lọ. Chùa Linh Phước là một công trình kiến trúc khảm sành đặc sắc của thành phố Đà Lạt</span>\r\n            </p>\r\n            <p >\r\n                <span >Trên đường về, đoàn ghé tham quan vườn hoa Cẩm Tú Cầu_nằm ở khu vực Trại Mát thuộc xã Xuân Thọ cách trung tâm thành phố Đà Lạt khoảng 15km với diện tích trồng lên tới 2ha. Nơi đây là một điểm du lịch vô cùng hấp dẫn giới trẻ và người yêu hoa tới chụp hình và check-in.</span>\r\n            </p>\r\n            <p >\r\n                <span >Về nhà hàng dùng cơm trưa.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe đưa du khách tham quan nhà thờ Domain De Marie_ còn được gọi là nhà thờ Mai Anh. Đây là một cụm kiến trúc bao gồm nhà nguyện và hai dãy nhà tu viện nữ tử Bác Ái Thánh Vinh Sơn với tổng diện tích là 12 ha nằm trên đường Ngô Quyền. Đây còn là địa chỉ tham quan hoàn toàn miễn phí cho bạn tận hưởng vẻ đẹp yên bình của nhà thờ theo lối kiến trúc đậm chất Châu Âu.</span>\r\n            </p>\r\n            <p >\r\n                <span >Kế tiếp đoàn sẽ tham quan Rừng Hoa Khô Đà Lạt_với nhiều loại hoa sấy khô đa sắc màu được các nghệ nhân cắm và trưng bày theo nhiều phong cách đầy sáng tạo, ấn tượng, hoặc biến hóa thành những con búp bê hoa ngộ nghĩnh, xinh xắn. Những bức tranh phong cảnh Đà Lạt được vẽ bằng hoa tươi sấy khô cũng là một trong những sản phẩm ấn tượng thu hút ánh nhìn của du khách khi đến với vườn hoa khô Đà Lạt.</span>\r\n            </p>\r\n            <p >\r\n                <span >Về nhà hàng dùng cơm tối. Quý khách tự do dạo chợ đêm Âm Phủ, tự do thưởng thức các món đặc sản: Kem Bơ Thanh Thảo (Nguyễn Văn Trỗi),…</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 3: MÊ LINH GARDEN COFFEE - ĐƯỜNG HẦM ĐIÊU KHẮC (ĂN 3 BỮA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Dùng buffet sáng tại khách sạn,</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa đoàn đi tham quan Mê Linh Garden Coffee_nằm cách xa trung tâm thành phố khoảng 25km, một đoạn đường khá dài qua con đèo Tà Nung. Được thiết kế theo một kiến trúc mở, không cầu kì như những quán khác. Bao quanh khu vực là rừng cà phê xanh mướt. Vừa để du khách thoát ra khỏi chốn đô thị về một miền đất nông thôn vừa để các bạn trẻ trải nghiệm cuộc sống xung quanh tràn ngập những màu sắc mới và cũng vô cùng thích thú.</span>\r\n            </p>\r\n            <p >\r\n                <span >Về nhà hàng dùng cơm trưa.</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Xe đưa du khách đến tham quan Đường Hầm Điêu Khắc - nơi thể hiện sự tài hoa của các nghệ nhân qua sự kết hợp tinh tế các tác phẩm điêu khắc từ đất sét kết nối những giá trị nhân văn của Đà Lạt từ thuở khai sơ cho đến ngày nay; ngoài ra Quý khách còn được tận mắt chiêm ngưỡng Ngôi nhà bằng đất sét duy nhất và độc đáo nhất nước ta, được Trung tâm sách Kỷ lục Việt Nam xác nhận 2 kỷ lục vào tháng 9/2013.</span>\r\n            </p>\r\n            <p >\r\n                <span >Tiếp tục đến đồi Rô Bin chụp hình và ngắm toàn cảnh Tp.Đà Lạt qua kính Viễn Vọng hay đi cáp treo (phí tự túc) đến viếng Thiền Viện Trúc Lâm: Ngôi chùa với nét kiến trúc độc đáo của Cao Nguyên Lâm Viên bên rặng núi Voi.</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa quý đoàn tham quan vườn dâu tây được xem là địa điểm “gây thương nhớ” cho rất nhiều du khách khi đến với “Thành phố sương mù”. Hình thức trải nghiệm khi tham quan, hái và mua dâu tại các vườn dâu tây sinh học, vườn dâu thủy canh hay những vườn dâu tây sạch được nhiều bạn thích thú.</span>\r\n            </p>\r\n            <p >\r\n                <span >Xe đưa đoàn đến nhà hàng dùng cơm tối với thực đơn hấp dẫn.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >NGÀY 4: ĐÀ LẠT - THÁC PRENN - HỒ CHÍ MINH (ĂN 2 BỮA)</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>\r\n            <p >\r\n                <span >Sáng: Quý khách trả phòng, dùng điểm tâm Buffet. Xe đưa du khách đi chợ Đà Lạt mua quà và đặc sản, tiếp tục đến với thắng cảnh thác Prenn: Du khách có thể đi thang máy xuống thác (chi phí tự túc). Khởi hành về đến Bảo Lộc dùng cơm trưa. Thưởng thức trà, cafe miễn phí</span>\r\n            </p>\r\n            <p >\r\n                <span >Chiều: Về đến Sài Gòn, xe và HDV du lịch Lữ Hành Việt đưa du khách về lại công ty. Chào tạm biệt Quý Khách.</span>\r\n            </p>\r\n            <p >\r\n                <span >&nbsp;</span>\r\n            </p>', 'On'),
(31, 6, 17, '687-tT', 'TOUR PRO GYMER', '2020-07-06', '5 ngày', '2800.000 ', '18.jpg', '1499744020-sapa-vyc1.jpg', 'about.png', '', '', 'TEst', 'Test', 'On');

